@-moz-document domain("youtube.com") {
@keyframes AnimationNameplaybar { 
    0%{background-position:0% 49%}
    50%{background-position:100% 52%}
    100%{background-position:0% 49%}
}
@keyframes AnimationNamezzz { 
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}

.html5-play-progress, .ytp-play-progress {
   background: linear-gradient(270deg, #ffffff, #ff0000, #ffab00, #fffa00, #0dff00, #00fff6);
background-size: 1200% 1200%;
   animation: AnimationNameplaybar 6s cubic-bezier(0.445, 0.05, 0.55, 0.95) infinite;
}
.html5-load-progress, .ytp-load-progress {
   background: linear-gradient(270deg, #000000, #878787, #000000, #747474, #000000);
background-size: 1000% 1000%;
animation: AnimationNamezzz 5s linear infinite;
}
