/* volume control */
.ytp-volume-slider-foreground:before {
   background: linear-gradient(270deg, #ffffff, #ff0000, #ffab00, #fffa00, #0dff00, #00fff6);
background-size: 1200% 1200%;
   animation: AnimationNameplaybar 3s cubic-bezier(0.445, 0.05, 0.55, 0.95) infinite;
}

/* MAKE THE ADS PRETTY, SO YOU CAN STARE AT THE COLORS, NOT THE ADS!!! YAY */
.videoAdUiSkipButton, .videoAdUiProgress, .videoAdUiTopBar, .videoadUiTitle, .videoAdUiTopBarWithGradients, .videoAdUiTopBarTransitions  {
    background: linear-gradient(270deg, #ffffff, #ff0000, #ffab00, #fffa00, #0dff00, #00fff6) !important;
background-size: 1200% 1200% !important;
   animation: AnimationNameplaybar 12s cubic-bezier(0.445, 0.05, 0.55, 0.95) infinite !important;
}
.videoAdUiProgress {
    height: 10px;
}
.videoAdUiTitle,.videoAdUiShare,.videoAdUiAuthorName { color:black; font-size: 20px; }
.videoAdUiProgressBar {
       background: linear-gradient(270deg, #000000, #878787, #000000, #747474, #000000);
background-size: 1000% 1000%;
animation: AnimationNamezzz 10s linear infinite;
    height: 10px;
}
.videoAdUiSkipContainer {
    opacity:1 !important;
}
.videoAdUi { 
    cursor: wait;
}

.videoAdUiSkipButton {
	color:black;
}

@keyframes AnimationNameads { 
    0%{background-position:0% 50%}
