css:
/* Medium blog sites sticky header/footer */
/* Cookie warnings pollute the web */
/* i really want this to be global */
.metabar.js-metabar.u-fixed {
	position: absolute !important;
}
.u-fixed.u-zIndexMetabar.js-stickyFooter {
	bottom: auto !important;
}
#js-gdpr-consent-banner, #noscript-warning, #sc-cookie-notification, .butterBar--privacy, #eu-cookie-bar-notification, .eu-cookie-bar-notification {
	display: none !important;
}

@-moz-document domain("tangerine.ca") {
	section.c-insights-carousel, div.content__what-i {
		display: none !important;
	}
}

@-moz-document domain("fedex.com") {
	[aria-label*="Virtual Assistant"] {
		display: none !important;
	}
}

@-moz-document domain("harvestapp.com") {
	canvas, .has-entries::after, .harvest-tooltip {
		display: none !important;
	}
}

/* Video autoplay on Ars. ARGH!! */
@-moz-document domain("arstechnica.com") {
.ars-video-playlist { display: none !important; }
}

/* Video autoplay on AccuWeather, damnit! */
@-moz-document domain("accuweather.com") {
	.vidWrapper, .page-column-2 {
		display: none !important;
	}
}

@-moz-document domain("lifehacker.com"), domain("kotaku.com") {
div.popular-box {
  display: none !important;
}
.toolbar {
  top: auto !important;
}
.sidebar {
  top: 60px !important;
}
}

@-moz-document domain("google.com"), domain("google.ca") {
a[href*="stuccu.c"], .waffle-assistant-entry {
    display: none !important;
}

}

@-moz-document domain("mail.google.com"), domain("mail.google.ca") {
a[href*="stuccu.c"], .waffle-assistant-entry, [aria-label="Add to tasks"], [data-tooltip="Add to tasks"], [title="Add to tasks"], [role="complementary"] {
    display: none !important;
}

}

@-moz-document domain("popsci.com") {
div.header-content {
  display: none !important;
}
}

@-moz-document domain("longecity.org") {
.attached {
    position: relative !important;
}
}

@-moz-document domain("quora.com") {
[class~="signup blur"] {
  display: none !important;
  visibility: hidden !important;
}
}

@-moz-document domain("twitter.com") {
.promoted-account, .promoted-trend, .promoted-tweet, .has-recap, li.separated-module, li[id^="stream-item-who_to_follow"], li[data-component-context="generic_activity"] { display: none !important; }

}

@-moz-document domain("inoreader.com") {
#loader_div, .bottom_notification {
    /* display: none !important; */
    left: 10px !important;
}
}

@-moz-document domain("www.swansonvitamins.com") {
.slidecartreveal {
    display: none !important;
}
}

@-moz-document domain("kijiji.com"), domain("kijiji.ca") {
.we-the-north-banner { display: none !important; }
}
