css:
@-moz-document domain(forums.daybreakgames.com) {
#loginBar {
color: rgb(42, 149, 225, 0.50);
background-color: #000;
position: relative;
z-index: 1
}
#loginBar .pageContent {
padding-top: 0;
position: relative;
_height: 0;
color: #bfbfbf
}
#loginBar a {
color: #bfbfbf
}
#loginBar form {
padding: 5px 0;
margin: 0 auto;
display: none;
line-height: 20px;
position: relative;
width: 500px;
height: 151px
}
#loginBar .xenForm .ctrlUnit,
