}
.nodeList .categoryStrip .nodeTitle a {
color: #fff
}
.nodeList .categoryStrip .nodeTitle {
font-size: 15px;
font-family: Arial, sans-serif;
color: #fff;
padding: 16px 5px 0 20px;
margin: 0
}
.nodeList .categoryStrip .nodeTitle a {
color: #fff
}
.nodeList .categoryStrip .nodeDescription {
font-size: 10px;
color: #dee0df;
padding-top: 0;
margin-top: -14px;
margin-right: 20px;
