css:
@-moz-document domain(jira.cambridge.org) {

.ghx-avatar {
    height: 24px ;
    overflow: hidden;
    right: 1px ;
    top: 1px ;
    width: 24px ;
}

.ghx-avatar img {
    -webkit-transition: width 0.2s, height 0.2s;
    height: 24px ;
    position: relative;
    width: 24px ;
}

.ghx-columns .ghx-column.ghx-wip-limit-exceeded {
    background-color: #d04437;
}

.ghx-extra-field[data-tooltip~="CodeReviewDone"], .ghx-extra-field[data-tooltip~="CodeReviewDone,"] {
    color: #3eb55e;
    display: list-item;
    font-size: 8pt;
    margin-bottom: 1px;
    padding-left: 2px;
    padding-right: 2px;
    width: 90%;
}

.ghx-extra-field[data-tooltip~="CodeReviewDone"], .ghx-extra-field[data-tooltip~="CodeReviewDone,"] > .ghx-extra-field-content::before {
    content: "? Code Review Done ................";
}

.ghx-extra-field[data-tooltip~="Fix"] {
    background-color: #3eb55e;
    color: #ffffff;
    font-size: 8pt;
    margin-bottom: 1px;
    padding-left: 2px;
    padding-right: 2px;
}

.ghx-extra-field[data-tooltip~="ForCodeReview"], .ghx-extra-field[data-tooltip~="ForCodeReview,"] {
    color: #FF8F45;
    display: list-item;
    font-size: 8pt;
    margin-bottom: 1px;
    padding-left: 2px;
    padding-right: 2px;
    width: 90%;
}

.ghx-extra-field[data-tooltip~="ForCodeReview"], .ghx-extra-field[data-tooltip~="ForCodeReview,"] > .ghx-extra-field-content::before {
    content: "?: For code review...................";
}

.ghx-extra-field[data-tooltip~="None"] {
    display: none;
}

.ghx-extra-field[data-tooltip~="Size:"] {
    background-color: #0052CC;
    color: #ffffff;
    font-size: 8pt;
    margin-bottom: 1px;
    padding-left: 2px;
    padding-right: 2px;
}

.ghx-extra-field[data-tooltip~="outcome:"] {
    color: #996633;
    font-size: 8pt;
    font-style: italic;
    margin-bottom: 2px;
    margin-top: 10px;
}

.ghx-key {
    font-size: 125%;
    font-weight: bold;
}

.ghx-type {
    display: none;
}

