    /*    font-style: italic;
    font-weight: bold;
    font-size: 18px; */
    display: none;
}

div.ghx-issue-content {
    font-size: 85%;
    padding-left: 12px;
}

div.ghx-issue.ghx-selected.ghx-type-1 {
    background: #ff9999 ;
}

