    color: #ffffff;
    font-size: 16px;
    font-weight: bold;
    height: 25px;
    width: 200%;
}

div.ghx-issue.ghx-type-10701 .ghx-days {
    display: none;
}

div.ghx-issue.ghx-type-10701 .ghx-key {
    display: none;
}

div.ghx-issue.ghx-type-10701 .ghx-summary {
    text-align: center;
    width: 80%
;
}

div.ghx-issue.ghx-type-10701 .ghx-type {
    display: none;
}

div.ghx-issue.ghx-type-108 {
    background: #99e5ff;
}

