}

div.ghx-issue.ghx-type-11400 .ghx-grabber {
    background: #ff99ff
;
}

div.ghx-issue.ghx-type-11400 .ghx-key {
    /*-- hide the ticket number for WIP defects --*/
    display: none;
    font-size: 6pt;
    position: absolute;
    top: 0;
}

div.ghx-issue.ghx-type-11400 .ghx-parent-stub {
    display: initial;
}

div.ghx-issue.ghx-type-11400:hover {
    background: #ff80ff ;
}

div.ghx-issue.ghx-type-122 {
    background: #99D0AB;
    height: 40px;
}

div.ghx-issue.ghx-type-122 .ghx-days {
    display: none;
}

div.ghx-issue.ghx-type-122 .ghx-key {
    /*-- hide the ticket number for policies --*/
    display: none;
    font-size: 6pt;
    position: absolute;
    top: 0;
}

div.ghx-issue.ghx-type-122 .ghx-summary {
    font-style: italic;
}

div.ghx-issue.ghx-type-122 .ghx-type {
    display: none;
}

div.ghx-issue.ghx-type-1:hover {
    background: #ffbbbb ;
}

div.ghx-issue.ghx-type-3 {
    background: #A3D1FF ;
}

div.ghx-issue.ghx-type-3:hover {
    background: #93BCE6 ;
}

div.ghx-issue.ghx-type-6 {
    background: #ffbd79;
}

div.ghx-issue.ghx-type-6:hover {
    background: #FFA347 ;
}

div.ghx-issue.ghx-type-7 {
    background: #fcfce0 ;
}

div.ghx-issue.ghx-type-7:hover {
    background: #fcfcbb ;
}

div.ghx-parent-stub {
    display: none;
}

div.ghx-qty {
    color: #7149f5;
}

div.ghx-swimlane-header span.ghx-description {
    color: orange;
}

div.ghx-swimlane-header[data-swimlane-id="1230"] {
    display: none;
}

div.ghx-swimlane-header[data-swimlane-id="1798"] {
    display: none;
}

div.ghx-swimlane-header[data-swimlane-id="1824"] {
    display: none;
}

div.ghx-swimlane-header[data-swimlane-id="1825"] {
    display: none;
}

div.js-detailview.ghx-issue {
    border: 1px solid #fff;
    margin-bottom: 3px;
}

div.js-detailview.ghx-issue.ghx-flagged {
    background: #FF6C47;
    border-color: #f51111;
    border-style: solid;
    border-width: 2px;
    font-style: italic;
}

div.js-detailview.ghx-issue.js-issue {
    max-width: 193%;
}

li.ghx-column {
    background-color: transparent;
}

li.ghx-column h2 {
    color: #2cbd49;
    font-size: 90%;
    font-weight: bold;
}

li.ghx-column.ui-sortable {
    background-color: transparent;
    border-top-color: #bababa;
}

li.ghx-column.ui-sortable:nth-child(1), li.ghx-column.ui-sortable:nth-child(3), li.ghx-column.ui-sortable:nth-child(5), li.ghx-column.ui-sortable:nth-child(7), li.ghx-column.ui-sortable:nth-child(8), li.ghx-column.ui-sortable:nth-child(9) {
    border-right: 1px dotted #999;
}

span.aui-badge {
    display: none;
}

span.aui-label {
    font-size: 8pt;
}

span.ghx-constraint.ghx-busted.ghx-busted-max {
    /*color: #ffffff;
    font-weight: bold;
    width: 120%;
    font-size: 14px;
    background-color: #d92534;
    text-align: center;*/
    display: none;
}

span.ghx-iconfont.aui-icon.aui-icon-small.aui-iconfont-flag {
    display: none;
}

span.ghx-inner {
    width: 120%;
}

span.ghx-priority img {
    display: none;
}

