css:
@-moz-document url-prefix("https://dev.go.utah.edu/cas/login"), regexp("https://go.utah.edu/cas/login.*") {
body {
    display: flex;
    flex-direction: column;
    background: #ffffff;
    min-height: 100vh;
    height: 100vh;
    font-family: 'Open Sans', -apple-system, BlinkMacSystemFont, 'Roboto', 'Segoe UI', 'Trebuchet MS', sans-serif;
    color: #444;
}
.cis-login {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}
#login {
    display: flex;
    flex-direction: column;
    flex: 1;
}
#body {
    flex: 1;
    background: #f7f7f7;
}
#content {
    display: flex;
    background: #ffffff;
    flex: 1;
}
#main-container {
    display: flex;
    background: #ffffff;
    flex: 1;
}
#main-container .wrapper {
    margin: auto;
}
#main-container #content {
    width: 100%;
}
#login-area form {
    margin: 0;
    border: 0;
    border-radius: 0;
}
.cis-login #login-area {
    padding: 1rem;
    margin: 0;
    background: #f7f7f7;
    border: 1px solid #cccccc;
    border-radius: 4px;
    font-size: 1.5rem;
}
#login-area .btn-group {
    margin-top: 1rem;
}
#login-area .btn-group input {
    margin: 0;
    border: 1px solid #cccccc;
    border-radius: 4px;
    width: 100%;
    background: #f0f0f0;
    color: #444;
    font-size: 1.5rem;
    text-transform: lowercase;
    font-weight: bold;
}
#login-area .btn-group input:hover {
    border: 1px solid #cccccc;
    border-radius: 4px;
    background: #e0e0e0;
}
#main-container #login-area {
    margin: 0;
}
.wrapper > div > h1 {
    text-align: left;
    padding: 1rem;
    padding-top: 0;
    margin: 0;
    margin-top: -4.25rem;
}
a[href="https://auxiliary.apps.utah.edu/uofu/misc/UnidRetrieval"] {
    position: absolute;
    top: 14.5rem;
    font-size: small;
}
a[href="https://system.apps.utah.edu/uofu/acs/uupasswd/portal-self-service"] {
    position: absolute;
    top: 15.75rem;
    font-size: small;
}
a[href="https://utah.instructure.com/login?canvas_login=1"] {
    display: none;
}
#status {
    position: absolute;
    top: 17.75rem;
    font-size: small;
}
@media(max-width: 480px) {
    #main-container {
        display: block;
        padding-top: 1rem;
    }
    #main-container .wrapper {
        margin: auto 1rem;
        width: auto;
    }
    .wrapper > div > h1 {
        display: none;
    }
    #header-container {
        display: none;
    }
    #footer-container {
        display: none;
    }
}
@media(min-width: 481px) {
    .wrapper > div > h1 {
        font-size: 3rem;
    }
    a[href="https://auxiliary.apps.utah.edu/uofu/misc/UnidRetrieval"] {
        position: absolute;
        top: 15.5rem;
    }
    a[href="https://system.apps.utah.edu/uofu/acs/uupasswd/portal-self-service"] {
        position: absolute;
        top: 16.75rem;
    }
    #status {
        position: absolute;
        top: 18.75rem;
    }
}
@media screen and (max-height: 690px) {
    #header-container, #footer-container {
        display: none;
    }
}
@media screen and (orientation:landscape) and (max-height: 480px) {
    h1 {
        display: none;
    }
}
.login-help {
    display: block;
}
#login-area #username,
#login-area #password {
    font-size: 2rem;
    width: 100%;
    max-width: 100%;
    margin: 0;
    border: 1px solid #cccccc;
}
#login-area #username {
    border-radius: 4px 4px 0 0;
}
#login-area #password {
    border-top: none;
    border-radius: 0 0 4px 4px;
}
#login-area label[for="username"],
#login-area label[for="password"] {
    position: absolute;
    visibility: hidden;
}
/* Hide the random paragraph messing up the margins. */
#login-area > p {
   display: none; 
}
#username,
#password {
    color: #999999;
    box-sizing: border-box;
}
.info-text {
    display: none;
}
.field input {
    display: inline-block;
    margin: auto;
}
.field > .columnNarrow {
    margin: 0;
}

.alert.alert-danger {
    position: absolute;
    top: 18rem;
    font-size: 1rem;
    padding-right: calc(1rem + 1px);
}

.alert.alert-danger > span {
    display: block;
    width: 100%;
}

#duo_form {
    margin-bottom: 0;
}
}

@-moz-document url-prefix("https://dev.go.utah.edu/cas/login") {
.wrapper h1::after {
    content: ': dev';
}
.login-help {
    display: none;
}
}

