css:
@-moz-document domain(mpcforum.pl) {/* online */
.ipsBadge_green { background: #5B2991; }
.ipsBadge_purple { background: #5B2991; }
.ipsBadge_grey { background: #5B2991; }
.ipsBadge_lightgrey { background: #5B2991; }
.ipsBadge_orange { background: #5B2991; }
.ipsBadge_red { background: #5B2991; }
.ipsBadge.has_icon.ipsBadge_green { background-color: #5B2991; }
.ipsBadge_lightgrey { background-color: #5B2991; }

/* strony */
.pagination .pages li.active {
background: #5B2991;
color: #fff;
padding: 1px 8px;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
border-radius: 3px;
cursor: default;
}
	

/* tlo strony */
#ipbwrapper {  
background-image: url(http://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-189131.jpg);
/* efekt paralax: */
min-height: 100%;
background-size: cover;
background-attachment: fixed;
background-repeat: no-repeat;
background-position: center center;
}

#branding {
opacity: 0.95
}

#content {
opacity: 0.95
}

/* obsługa zbanowanych */
td.col_f_post > ul
li {
color: black;
}

div.post_username a:link,hover,active,visited{
color: white;
font-weight: bold;
}

.post_username{
float: left;
min-width: 177px;
font-weight: normal;
color: black;
}


/* nawigacja */
#secondary_navigation a{
color: #7E7E7E;
line-height: 37px;
height: 37px;
}
#secondary_navigation a:hover{ color: #804CB8; }

/* kolor tekstu */
a {
color: silver;
}
a:hover { color: #7E7E7E; }

#index_stats a{ color: silver; }
#index_stats a:hover{ color: white; }

/* reputacja */
.maintitle {
background: url("http://www.mpcforum.pl/uploads/images/1808614318795861204585.png");
color: white;
text-shadow: rgba(0,0,0,0.25) 0px -1px 0px;
padding: 12px;
font-size: 12px;
font-weight: 300;
-moz-border-radius: 4px;
-webkit-border-radius: 4px;
border-radius: 4px;
overflow: hidden;
margin-bottom: 4px;
-webkit-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
}
.reputation.positive, .members li.positive {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0;
}
       
.reputation.negative, .members li.negative {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0;
}
       
.reputation.positive, .reputation.negative {
color: #fff;
}
       
.reputation.zero {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0;
color: #fff;
}

/* wyszukiwarka */
#search .submit_input {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/search_icon.png') no-repeat 50%;
text-indent: -3000em;
padding: 0; border: 0;
display: block;
width: 26px;
height: 26px;
position: absolute;
right: 0; top: 0; bottom: 0;
-moz-border-radius: 0 3px 3px 0;
-webkit-border-top-right-radius: 3px;
-webkit-border-bottom-right-radius: 3px;
border-radius: 0 3px 3px 0;
-webkit-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
-moz-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
}
 
#search .submit_input:hover{ background-color: #804CB8; }
		
/* buttony */
.topic_buttons li a, .topic_buttons li span, .ipsButton {
background: #5B2991;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
border-radius: 3px;
color: #fff;
text-shadow: 0 -1px 0 rgba(0,0,0,0.2);
font-size: 12px;
font-weight: bold;
line-height: 34px;
height: 34px;
padding: 0 20px;
text-align: center;
min-width: 70px;
display: inline-block;
cursor: pointer;
-webkit-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
}
.topic_buttons li a:hover, .ipsButton:hover { background-color: #804CB8; color: #fff; }
		
/* posty */
ul.post_controls a {  
color: #fff;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
background: #5B2991;
-webkit-border-radius: 2px;
-moz-border-radius: 2px;
border-radius: 2px;
height: 26px;
line-height: 26px;
padding: 0 10px;
text-decoration: none;
margin-left: 4px;
display: block;
}

ul.post_controls a:hover { background-color: #804CB8; color: #fff; }

/* lajkacz */
.ipsLikeButton.ipsLikeButton_enabled {
background: #5B2991;
}
	
.ipsLikeButton.ipsLikeButton_enabled:hover {
background: #804CB8;
}

/* dodawanie postów */
.input_submit {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0;
border-color: #3f3f3f;
color: #fff;
text-shadow: rgba(0,0,0,0.4) 0px -1px 0px;
-moz-box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-webkit-box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
border-radius: 2px;
}
 
.input_submit:hover {
background-color: #804CB8;
color: #fff;
}

/* popup */
.message {
background: #5B2991 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 -1px;
padding: 10px;
border: 1px solid #804CB8;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
line-height: 1.6;
font-size: 12px;
-webkit-box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
-moz-box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
}
}
