css:
@-moz-document domain("youtube.com") {

/* change icons */

.gsok_a, 
.comments .sprite_dropdown,
.comments .sprite_like,
.comments .sprite_dislike,
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell {
	opacity: 1.0 !important; 
	-webkit-filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
	filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
}

/***  inputs ***/
/* menu */
.gsfs {
	background-color: #000000 !important;
	color: #167AC6 !important; 
}

.gssb_m:hover {
	background-color: #167AC6 !important;
}

#masthead-search-terms {
	-moz-appearance: none;
	background-color: #000000 !important;
	border-radius: 1px !important;
	border-color: #ffffff !important;
	border-width: 1px !important;
    box-shadow: none !important;
	transition: none !important;
}

#masthead-search-terms input {
	color: white !important;
	background-color: #000000 !important;
}

/* input 2 - in canals searching */
.yt-uix-form-input-fluid .yt-uix-form-input-text {
	background-color: #000000 !important;
	color: #ffffff !important;
	width: 95% !important; 
}

/*  buttons */
.yt-uix-button-primary {
	background: #167AC6 !important;
	color: black !important; 
	border-color: #ffffff !important;
	border-width: 1px !important;
	border-radius: 1px !important;
	margin-left:0px !important;
}

#upload-btn,
.channels-search .search-button, /* search canals */
#masthead-search .search-btn-component {
	opacity: 1.0  !important; 
	-moz-appearance: none;
	border-width: 0px !important; 
	height: 30px !important;
	border-radius: 1px !important; 
	border: 1px solid #ffffff !important; 
	color: black !important; 
	margin-left: 5px  !important;
	background: #167AC6 !important;
}

#masthead-search .search-btn-component .yt-uix-button-content {
	opacity: 1.0  !important; 
}

.yt-uix-subscription-preferences-button,
.branded-page-box  .yt-uix-button-default,
#footer-main .yt-uix-button-default {
	background: #167AC6 !important;
	color: black !important; 
	border-color: #ffffff !important;
	border-width: 1px !important;
	border-radius: 1px !important;
	margin-left:0px !important;
}

#watch8-secondary-actions {
    left: 0px  !important;
}

#action-panel-overflow-button {
    margin-left: 1px  !important;
}

#formats-button-small {
    margin-left: 20px  !important;
}

.yt-uix-button-subscribe-unbranded,
.yt-uix-button.yt-uix-button-subscribed-unbranded {
	-moz-appearance: none;
	color: black !important; 
	border-width: 0px !important; 
	height: 30px !important;
	border-radius: 1px !important; 
	border: 1px solid #ffffff !important; 
	color: black; 
	height: 20px !important;
	width: 150px !important;
	background: #167AC6 !important;
}

#watch8-action-buttons .yt-uix-button, 
#watch8-action-buttons .yt-uix-button:hover, 
#action-panel-dismiss, 
#action-panel-dismiss:hover {
	-moz-appearance: none;
	border-width: 0px !important; 
	height: 30px !important;
	border-radius: 1px !important; 
	border: 1px solid #ffffff !important; 
	color: black !important; 
	background: #167AC6 !important;
}

/* backgrounds */
body, #yt-masthead-container,
#yt-picker-country-footer, 
#yt-picker-language-footer, 
#yt-picker-safetymode-footer {
	background-color: #333230 !important; 
}

.channel-header .secondary-header-contents,
.yt-card,
.yt-ui-ellipsis,
.individual-feed,
#appbar-nav,
#appbar-guide-menu,
#footer-container {
	background-color: #000000 !important; 
}

.guide-item.guide-item-selected  {
	-moz-appearance:none !important;
	background-color: #167AC6 !important;
}

.guide-item:hover  {
	-moz-appearance:none !important;
	background-color: #167AC6 !important;
}

/* unsigned */
li.guide-section h3 {
	-moz-appearance:none !important;
	color: #167AC6 !important;
}

.guide-item .display-name {
	color: white !important; 
}

/* colours of texts */
/* white colour */
.yt-card .yt-uix-tabs .yt-uix-button,
.watch-card .yt-uix-tabs-tab,
#watch7-sidebar .watch-sidebar-head,
.comments .comment-footer-action,
.video-list .video-list-item .stat,
#watch-description,
.comments .comment-text .comment-text-content,
#upload-drag-drop-description,
.upload-informational-footer,
.upload-other-options-list .upload-option-text,
#yt-masthead #logo-container span.content-region,
#subscription-manager-container .empty-message,
.yt-gb-shelf-hero-content .description,
.c4-checklist-module .checklist-item-title-text,
.secondary-header-contents .nav-text,
.search-header .num-results strong,
.search-header .num-results,
.filter,
#safety-form label,
.yt-default ul,
p.yt-notes,
#footer-links-secondary a,
#footer-links-primary a,
.yt-lockup,
.shelf-annotation,
.branded-page-module-title a,
.guide-item .guide-count,
.guide-item .display-name {
	color: #ffffff !important;
}

/* blue colour */
.yt-card .yt-uix-button-expander,
.video-list-item .title,
#watch7-headline h1,
.upload-help-link-list .help-item,
#upload-button-text,
.upload-footer-header,
.upload-sidebar-header,
li.guide-section h3 a,
#subscription-manager-container .subscription-title, #subscription-manager-container .collection-title,
.branded-page-related-channels h3 a, .branded-page-related-channels h3,
.category-header .category-title,
.pl-video-title-link,
#pl-header .pl-header-title,
.branded-page-module-title,
.primary-header-contents .view-as-menu .view-as-menu-trigger,
.channel-header .branded-page-header-title .branded-page-header-title-link,
.filter-col-title,
.filter:hover,
.yt-picker-header h3.yt,
#footer-links-secondary a:hover,
#footer-links-primary a:hover,
.branded-page-module-title a:hover {
	color: #167AC6 !important;
}

/* colour of video thumbnails */
.video-list-item .yt-uix-simple-thumb-wrap,
.yt-lockup-thumbnail {
	border: 1px solid #ffffff !important;
}

.video-list-item .yt-uix-simple-thumb-wrap:hover,
.yt-lockup-thumbnail:hover {
	border: 1px solid #167AC6 !important;
}

#player-playlist :not(.watch-queue-nav) .playlist-videos-list > li.currently-playing .index {
    color: #167AC6 !important;
}

#player-playlist :not(.watch-queue-nav) .currently-playing .playlist-video .video-thumb {
    border: 2px solid #167AC6 !important;
}

.yt-card .yt-uix-tabs .yt-uix-button:hover, .yt-card .yt-uix-tabs .yt-uix-button:active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-active, .yt-card .yt-uix-tabs .yt-uix-button.yt-uix-button-toggled,
/* buttons in appbar nav */
a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
	color: #ffffff !important;
	border-color: #167AC6 !important;
}

a.yt-uix-button-epic-nav-item:hover,
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
	color: #ffffff !important;
	border-color: #167AC6 !important;
}

a.yt-uix-button-epic-nav-item,
a.yt-uix-button-epic-nav-item,
a.yt-uix-button-epic-nav-item.yt-uix-button{
	color: #ffffff !important;
	border-color: #167AC6 !important;
}

.watch-card .yt-uix-tabs-selected {
	color: #ffffff !important;
	border-color: #167AC6 !important;
	border-width: 3px !important;
}

.all-comments, .all-comments a {
	color: #ffffff !important;
	background-color: #000000 !important;
}

.comments .paginator {
	color: #167AC6 !important;
	background-image: none !important;
	background-color: #000000 !important;
}

/* icons */

#footer-logo .footer-logo-icon,

#yt-masthead #logo-container .logo {
  background: no-repeat url(data:image/png;base64,) !important;



  opacity: 1.0  !important;

}



/* change colour guide button */

.show-guide .yt-uix-button-icon-appbar-guide  {

  background: no-repeat url(data:image/png;base64,)  !important; 

  opacity: 1.0  !important;

}


.show-guide #appbar-guide-button .yt-uix-button-arrow {
border-top-color: #167AC6 !important;
}

/* opacity for good contrast */
.comments .comment-item .mod-button:hover .default-state.sprite_like, 
.comments .comment-item .mod-button:hover .default-state.sprite_dislike,
.comments .comment-item .mod-button .default-state.sprite_like, 
.comments .comment-item .mod-button .default-state.sprite_dislike {
    opacity: 1.0  !important;
}

.yt-uix-button,
.yt-uix-button-icon {
	opacity: 1.0  !important; 
}

.yt-uix-button-arrow {
border-color: #000000 rgba(0, 0, 0, 0.0) rgba(0, 0, 0, 0.0) !important;
}

.comments .footer-button-bar {
	border: 1px #ffffff !important;
}

.yt-uix-button-subscribe-branded {
	background-color: #167AC6 !important;
}

/* change colour loading bar */
#progress {
	height: 5px !important;
    background: #167AC6 !important;
}

/****** support for html5 player *****/
.html5-play-progress, .ytp-play-progress {
	background: #167AC6 !important;
}

/* actual scrubber button */
.html5-scrubber-button, .ytp-scrubber-button {
	background: #383A3B !important;
	border-color: #1B1B1B !important;
	box-shadow: 0px 0px 3px #167AC6, 0px 0px 5px #167AC6, 0px 0px 0px #167AC6 !important;

}

/* scrubber button on hover */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
	background: #167AC6 !important;
	border-color: #1B1B1B !important;
	box-shadow: 0px 0px 6px #167AC6, 0px 0px 8px #167AC6, 0px 0px 10px #167AC6 !important;
}

/* volume control */
.ytp-volume-slider-foreground:before {
	background: #167AC6 !important;
}

/* end youtube section */
}
