/**
 Created on 2015-05-19 by Pierre Dumuid
 */

@-moz-document domain('console.aws.amazon.com') {

#awsgnav #nav-shortcutMenu .nav-elt-label,
#awsgnav .service-link .service-label,
#awsgnav .nav-menu .nav-elt-label {
    /* Reduce the font size */
    font-size: 11px;
}

#awsgnav #nav-menu-right .nav-elt-label {
    /* Change the font-size of the top-right drop-downs. */
    font-size: 9px;
}


#h #awsgnav #nav-menubar {
    /* Reduce the margin between nav-bar and "Amazon Web Service" title. */
    margin : 0px 0px 3px 0px;
    
    /* Reduce the height of the nav-bar. */
    height : 27px;
}


/* Reduce padding around the top-left corner logo. */
#awsgnav #nav-logo {
    margin: 4px 3px 0px 4px;
}

/* Shift top of the drop-down arrow */
#awsgnav .nav-menu-arrow {
    top: 11px;
}
/* Move the location of the drop-down arrow.*/
#awsgnav #nav-servicesMenu .nav-menu-arrow, #awsgnav #nav-resourceGroupsMenu .nav-menu-arrow {
    right: 0px;
}

/* Shift top of the drop-down arrow */
#awsgnav #nav-menubar .nav-menu-separator {
    margin: 4px;
    height: 19px;
}

#awsgnav #nav-servicesMenu, #awsgnav #nav-resourceGroupsMenu {
    /* Reduce the height  of AWS drop-down menu. */
  padding: 4px 0px 0px;
}

#awsgnav #nav-servicesMenu .nav-elt-label, #awsgnav #nav-resourceGroupsMenu .nav-elt-label, #awsgnav #nav-shortcutMenu .nav-elt-label {
     padding: 0 10px 0 3px; 
}


#awsgnav #nav-shortcutBar {
    /* Reduce padding of the short-cuts to eact section. */
    padding: 0;
}

#awsgnav #nav-shortcutBar a {
    /* Change the "top" position of the short-cuts texts. */
    padding: 3px 0px 0px;
}

#awsgnav .service-icon-wrapper {
    top: -2px;
}

#awsgnav #nav-menu-right {
    /*Force right menu to hug the right corner of the page. */
    padding: 0px;
}

#awsgnav .nav-menu {
    /* Still ugly, but move the height of the "Edit" and account / region / support menu's. */   
  padding: 4px 7px 6px;
}

#awsgnav #nav-shortcutBar .service-link {
    /* horizontal spacing.Remove the */
    margin: 0;
}
#awsgnav .service-link .service-label {
      /* Distance from icon to text. */
      padding-left: 1px;
    
}

#awsgnav #nav-shortcutBar a {
    /* Width of service items. */
    margin: 1px 0px;
}
                                        

}
