    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.2);
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
    border-radius: 2px;
    margin-top: 3px;
    margin-bottom: 3px;
  }
  
  .moveselect button:focus, .switchselect button:focus {
    background: #F6F7F8;
    color: #333;
    text-shadow: none;
  }
  
  .switchmenu button, .switchmenu button:hover {
    font-family: helvetica,arial,"lucida grande",sans-serif;
    background: #4E69A2;
    border-color: #435A8B #3C5488 #334C83;
    color: #FFF;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.2);
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
    border-radius: 2px;
  }
  
  .switchmenu button.disabled, .switchmenu button:disabled {
    background: #F6F7F8;
    color: #333;
    text-shadow: none;
  }
  
  .switchmenu button .hpbar {
    border: 1px solid #FFF;
    opacity: 0.9;
  }
  
  .turn {
    border-color: #CCC #C5C6C8 #B6B7B9;
    color: #4E5665;
    text-shadow: 0px 1px 0px #FFF;
    background-color: #F6F7F8;
    background-image: url("http://i.imgur.com/ksxZri3.png?1");
    background-repeat: repeat-x;
    border: 1px solid;
    border-radius: 2px;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
    font-family: helvetica,arial,sans-serif;
    font-weight: bold;
  }
  
  .leftbar, .rightbar {
    border-color: #CCC #C5C6C8 #B6B7B9;
    color: #4E5665;
    text-shadow: 0px 1px 0px #FFF;
    font-family: helvetica,arial,sans-serif;
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
  
  /* Battle CSS */
