css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.co.uk") {
  body {
    background-color: #222;
  }
  
  a:link, .w,  #prs a:active, .q:active, .kl:active, .tbotu {
    color: #FFF;
  }
  a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b {
    color: #E6E6E6;
  }
  .q:visited, #prs a:visited, a:visited {
    color: #D6D6D6;
  }
  #epbar {
    display: none!important;
  }
  
  #hdtbSum {
    background-color: #454545;
  }
  
  /* logo */
  #hplogo {
    width: 120px!important;
    height: 77px!important;
  }
  #hplogo div {
    display: none;
  }
  
  /* search buttons */
  .jhp input[type="submit"] {
    background-color: #040002;
    background-image: none;
    cursor: pointer;
    border: 0;
    border-radius: 0;
    font-size:13px;
  }
  .jhp input[type="submit"]:hover {
    background-color: #CCC;
    background-image: none;
    cursor: pointer;
    border: 0;
    border-radius: 0;
    font-size:13px;
  }
  .jhp input[type="submit"]:nth-child(2) {
    display: none;
  }
}
