css:
@-moz-document regexp("^.+tfs.+:80.*$") {
.board-view {
background: #555555;
}
  
.hub-content, .combo-drop-popup {
background: #222222;
color: #CCCCCC;
}

.main-content {
background: #222222;
color: #CCCCCC;
}

.grid-gutter .grid-gutter-cell .grid-gutter-drop .grid-gutter-menu {
background: #222222;
}

.grid-gutter-row, .vc-history-list .results-container .group-header {
background: #222222 !important;
}

            
.blank-message {
background: #332233;
color: #CC9900;
}
  
.node-content {
color: #CCCCCC !important;
}

.pivot-filter-item a {
color: #CCCCCC;
}
  
.text, .header-post11 .hub-groups-section li a, .header-post11 .hubs li a, .vc-history-basic-filter .selected-filter, .ui-widget-content {
color: #CCCCCC;
}

.hub-pivot-content {
background: #222222;
}
  
.board-tile {
background: #333344 !important;
}

.taskboard-cell .witTitle {
color: #999999;
}
    
.taskboard-cell .witRemainingWork {
color: #CCCCCC;
}

.tbTile .tbTileContent {
background: #332233;
border: none;
}

.tbTile .tbTileContent .witTitle {
  color: #999966;
}

.header-post11 #header-row {
background: #222222;
}

.header-post11 #navWrapper {
background-color: #222222;
}

.header-post11 .header-search > .search-text {
background-color: black;
}

.header-post11 .header-search {
background-color: black;
}

.sidebar-search .search-input-wrapper {
border: 1px solid black;
background-color: #222222;
}

.tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content {
background-color: #222222;
border: 1px solid black;
}
 
.tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content {
background-color: #222222;
border: 1px solid black;
}

.grid-gutter-row-normal {
border: 1px solid transparent;
}

.grid-row-current {
border: 1px solid black;
}

.splitter > .handleBar, .splitter > .handleBar-clone {
background-color: black;
}

.hub-splitter.splitter.horizontal > .leftPane {
border-right: 1px solid black;
}

.build-detail-view .header {
background-color: #222222;
border-bottom: 1px solid black;
}

.toolbar {
border-top: 1px solid black;
border-bottom: 1px solid black;
}


.tree-view > ul.tree-children li.focus > a > div.node-content, 
.tree-view > ul.tree-children li.hover > a > div.node-content, 
.tree-view > ul.tree-children li.context-menu-active > a > div.node-content
{
background-color: #222222;
border: 1px solid black;
}

.hub-title {
color: #CCCCCC;
}

.hub-pivot {
color: #444444;
}

.hub-pivot {
background: #333333;
}

.hub-pivot a {
color: #CCCCCC;
}

.menu,
.toolbar .menu-bar .menu-item.hover,
.workitemcontrol .menu-bar .menu-item.hover {
background-color: #222222; 
}


.add-panel {
background-color: #222222;
border-left-color: black;
}

.combo {
border: 1px solid black;
background: #222222;
}

.ui-dialog-buttonpane {
background-color: #222222;
border: 1px solid black;
}

input {
background: #222222;
color: #CCCCCC;
}

.ui-dialog
{
background-color: #444444;
}

body {
color: white;
background-color: #222222;
}

.ui-tabs .ui-tabs-nav {
background-color: #222222;
}

.ui-widget-content {
border: 1px solid black;
}

.ui-dialog .ui-dialog-titlebar-close {
background-color: #666666;
}
            
.grid-row {
color: #CCCCCC;
}

.grid-row:hover {
background: #442233;
}

.grid-row-current {
background: #333333;
}

.grid-row-selected {
background: #332233 !important;
}

.grid-row-selected-blur {
background: #332233 !important;
}

.ui-dialog .ui-dialog-titlebar {
background-color: #333333;
border-bottom: 1px solid black;
}

.ui-dialog .ui-dialog-title {
color: white;
}

.splitter > .handleBar.hover
{
    background-color: #444444;
}

.splitter > .handleBar,
.splitter > .handleBar-clone
{
    background-color: black;
}

body {
color: white;
}

.content-section {
background-color: #222222;
}

.group-info-input {
color: white;
}

.versioncontrol-explorer-view .source-path {
border: 1px solid black;
background-color: #222222;
}

.visual-progress-total-unallocated {
background-color: #444444;
}

.ui-widget-overlay {
background: #332233;
}

.splitter.toggle-button-enabled.collapsed > .handleBar {
background-color: black;
}

.queue-build select {
border: 1px solid black;
}

input, textarea, select {
background-color: #222222;
}

.queue-build input[type='text'] {
border: 1px solid black;
}

.workitem-info-bar {
color: white;
}

.ui-tabs .ui-tabs-nav li.ui-tabs-selected a {
color: white;
}

.ui-tabs .ui-tabs-nav li a {
color: white;
}

input, button, textarea, select {
color: white;
background-color: #222222;
}

.modal-dialog label {
color: #cccccc;
}
}
