#logo{
  background: /*[[Color]]*/ none repeat scroll 0% 0%;
  padding: 10px 0;
   
}
/*[[BG2]]*/
body {
  background: #fff scroll 0% 0%;
  background-attachment: scroll;
  background-color: rgb(233, 234, 237);
}
#panel .upper {
  color: #FFF;
  padding: 7px;
  clear: both;
  border-top: 1px solid white;
  border-top: /*[[Color]]*/;
  background: /*[[Color]]*/ none repeat scroll 0% 0%;
  height: 22px;
}
.thead {
   background:/*[[Color]]*/ none repeat scroll 0% 0%;
  padding: 8px;
  color: #FFF;
}
.forum_status {
  height: 30px;
  width: 30px;
  background: url(http://i.imgur.com/Z0TLQkB.png) no-repeat 0 0;
  display: inline-block;
}
a.button span {
  padding-left: 0px;
  display: inline-block;
  background:/*[[Color]]*/
}
.post .post_author div.author_avatar img {
  padding: 5px;
  background:/*[[Color]]*/ none repeat scroll 0% 0%;
}
a.button:link, a.button:hover, a.button:visited, a.button:active {
  display: inline-block;
  padding: 4px 8px;
  margin: 2px 2px 6px;
  border:/*[[Color]]*/;
  font-size: 14px;
  background:/*[[Color]]*/
}
button, input.button {
  padding: 3px 8px;
  cursor: pointer;
  font-family: Tahoma, Verdana, Arial, Sans-Serif;
  font-size: 13px;
  background: rgb(255, 255, 255)
}
a:link {
  color:/*[[Color]]*/;
  text-decoration: none;
}
a:visited {
  color:/*[[Color]]*/;
  text-decoration: none;
}
