css:
/* i really want this to be global */
#editor-div{
	background : none repeat scroll 0 0 #f5f5f5 !important;
}
button#quickSlideshow{
	background : #C24500!important;
}
#slidecontainer{
	box-shadow : none !important;
}
.main_renderer_container{
	box-shadow : none !important;
}
.ui-zeditor-medium-background .main_renderer_container{
    box-shadow : none !important;
    border : 1px solid #9d9d9d !important;
}
.ui-ztopband-background{
	background: none #f2f2f2 !important;
}
#saveStatus{
	color : #554141;
	text-shadow : none;
}
.ui-save-message{
	border-right : 1px solid #C24500;
}
.ui-toplinks-div span{
	font-weight : 700;
    color : #333 !important;
    text-shadow : none !important;
}
div#menutabs{
	box-shadow : none;
}
.ui-ztab-menutab .ui-tabs-panel, .ui-ztab-bg{
	background-color : #fff;
}
.ui-tabs .ui-tabs-nav .ui-state-default{
	background: none !important;
    border-radius : 0px !important;
    border : none !important;
}
.ui-widget-shadow-new{
	box-shadow : none;
}
#thumbnailtool_slide{
	border-radius : 0px;
}
#splitMenu{
	border-radius : 0px;
}
.ui-zeditor-medium-background .ui-zsplitpane-divider-x.ui-state-active{
	border-left-color : #AE3E00 !important;
}
#quickslideshow .ui-button-splitbutton.ui-buttonset.ui-state-hover .ui-slideshow-btn.ui-state-hover {
    background: #AE3E00 !important;
}
#quickslideshow .ui-button-splitbutton.ui-buttonset.ui-state-hover .ui-button.ui-state-default,#quickslideshow .ui-button-splitbutton.ui-state-highlight .ui-state-default,#quickslideshow .ui-slideshow-btn.ui-state-hover,#quickslideshow .ui-slideshow-btn.ui-state-focus,#quickslideshow .ui-slideshow-btn.ui-state-active,#quickslideshow .ui-slideshow-btn.ui-state-highlight,#quickslideshow button.ui-state-hover {
    background : #AE3E00 !important;
}
#quickslideshow #splitMenu.ui-splitbutton.ui-state-hover,#quickslideshow #splitMenu.ui-state-highlight {
    background: #C24500 !important;
}
.ui-ztab-menutab .ui-tabs-nav li.ui-ztab-menu-only a{
	background:#C24500 !important;
	height: 24px !important;
      width: 40px;
  color: #fff;
  text-shadow: none;
  font-size: 17px;
}
#fileMenu{
	  border-radius: 0px;
  height: 24px !important;
  width: 40px;
   font-size : 15px;
  text-shadow: none;
  color: #fff;
}
.ui-tabs-inner-panel{
	height : 30px;
}
.ui-ztab-menutab .ui-tabs-panel{
	padding : 0px 5px 5px 5px;
}
.ui-ztab-menutab .ui-tabs-nav li{
	height : 26px !important;
}
.ui-ztab-menutab .ui-tabs-nav li a{
	padding : 2px 20px !important
}
.ui-ztab-menutab .ui-tabs-nav li.ui-ztab-menu-only{
	border : none;
}
.ui-ztab-menu-only span.ui-ztab-dropdown{
	padding : 8px 0;
}
.ui-zmenu-group{
	background-color: #ccc ;
  color: #333;
}
.ui-tabs .ui-tabs-nav .ui-tabs-selected.ui-state-active{
	border : 1px solid #b9b9b9 !important;
    height : 25px !important;
}
span.ui-ztab-dropdown.ui-ztab-arrow{
	display : none !important;
}
.ui-ztab-menutab .ui-tabs-nav li.ui-tabs-selected a{
	background : #fff !important;
}
#file span.ui-ztab-dropdown.ui-ztab-arrow{
        display : block !important;
}
#quickslideshow #splitMenu.ui-splitbutton{
	background-color: #C24500 !important
}
