css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instagram.com") {

body .top-bar {
	background:#517fa4;
	box-shadow:none;
	border:0;
	height:44px;
}
body .top-bar-actions,
body .top-bar-actions>li>a,
body .top-bar-actions>li>a.link-active {
	border:0;
	box-shadow:none;
	text-shadow:none;
}

body span.img-outset,
body pan.img-inset,
body .img-outset b,
body .img-inset b {
	border-radius:0;
	box-shadow:none;
}


body.timelineFeedPage {
	background:#eee;
}
body .timelineCenter {
	box-shadow:none;
	background:none;
	border:0;
}
body .timelineCard {
	background:#fff;
	box-shadow:none;
	border:0;
	border-radius:0;
}

body .timelineBookmarkAvatar:after,
body .timelineCommentAvatar:after {
	display:none;
}

body .timelineTopPaginate {
	box-shadow:none;
}

body .timelineLikes {
	background:#f5f5f5;
	box-shadow:none;
	border:0;
	margin:0;
}
body .timelineLikeButton,
body .timelineLikes .mediaMoreButton {
	border:0;
}
body .timelineCaption.timelineComment {
	box-shadow:none;
}

body .dropdown ul.dropdownOptionList, .dropdown ul {
	border-radius:0;
}

/* PHOTO PAGE */

body .DesktopPPage .Item,
body .ResponsiveContainer .iMedia,
body .ResponsiveContainer .Frame,
body .ResponsiveContainer .Item .Info {
	border:0;
	border-radius:0;
	box-shadow:none;
}

}
