css:
@-moz-document domain("www.reddit.com") 
{
body, table, tr, td, .promoted, #sr-header-area, .dropdown, #header-bottom-left, #header-bottom-right, .side, .morelink, li, .selected, .selected a, .choice, #sr-more-link, .raisedbox, .entry
{
background-color: #000 !important; 
color: white !important; 
border: 0px !important;
}
.even, .odd{background-color: #000 !important; color: white !important;
}
input{background-color: black ! important; color: white ! important;
}
h3, .label, a{color: rgb(60,120,180) !important;
}
a:hover{color: rgb(199,120,110) !important;
}
.title a{color: rgb(100,150,190) !important;
}
.title a:hover{color: rgb(100,160,260) !important;
}
a:link
{
color: rgb(100,150,190) !important;
}
a:visited
{
color: rgb(190,140,200) !important;
}
#header{border-bottom: 0px !important;
}
#header-bottom-left{background-color: rgb(205,225,255) !important;
}
.infobar, .commentbody.border{background-color:#000 ! important;
}
.footer{background-color: #333 !important; -moz-border-radius-topleft: 25px; -moz-border-radius-topright: 25px;
}
.footer *{background-color: #333 !important;
}
.tabmenu li, .tabmenu a, .tabdrop, .tabdrop span
{
-moz-border-radius-topleft: 5px !important; -moz-border-radius-topright: 5px !important; background: #222 !important;
}
.usertext-body .md
{
background: #000 !important;
}
.redditname a
{
background-color: rgb(59,140,220) !important; 
color: #fff !important; 
-moz-border-radius-topleft: 5px !important;
 -moz-border-radius-topright: 5px !important;
 padding: 0px 5px 0px 5px !important;
}

a[href="/r/nsfw/"]
{
font-weight: bold !important; background-color: white !important;
 color: #000 !important; font-size: large !important;
}
.commentbody div a{color: red !important;
}
li.selected > a{color: rgb(255,130,150) !important;
}
#ad-frame, #ad, #adlink{display: none !important;
}
div.side div.spacer > ul.flat-list > li > a
{
background: #444 !important; 
border: 0px !important;
}
div.comment > div.child {border-left: dotted 2px #555 !important;
}
}
