css:
@-moz-document url-prefix("https://web.telegram.org") {
html {
	background: /*эмблема*/ url(https://img-fotki.yandex.ru/get/28032/59844696.0/0_a97be_f95e562_orig) fixed no-repeat 100% 90%,
		/*надпись*/ url(https://img-fotki.yandex.ru/get/9753/59844696.0/0_a721c_3d004d5b_orig) fixed no-repeat 99% 99%,
			/*фон*/ url(https://img-fotki.yandex.ru/get/5106/59844696.0/0_9c61c_b3d81a61_orig) fixed center no-repeat;
	background-size: auto, auto, cover;
}
	
	.im_message_webpage_wrap {
		line-height: 32px;
	}
	
	.im_dialog_message_wrap {
		font-size: small !important;
	}
	
	.emoji {
		vertical-align: middle;
	}
	
.im_message_wrap {
	background: rgba(0, 0, 0, 0.6) !important; /* фон блока сообщений */
	max-width: 100%;
}
	
.im_page_wrap {
	background: rgba(0,0,0,0) !important;
	border: none !important;
	box-shadow: none !important;
	max-width: 70% !important;
}
	
.tg_head_split {max-width: 70%;}

body,
a.tg_checkbox,
.contacts_modal_members_list a.contacts_modal_contact,
a.tg_radio {
	color: #fff !important;
}	

.im_dialogs_col_wrap {
	border: none !important;
	width: 25% !important;
	margin-right: 1% !important;
	background: rgba(0,0,0,0.5) !important;
	
}
	
	.im_history_col_wrap {
	  width: 69% !important;
	  /*float: right !important;*/
	}
	.tg_head_main_wrap {width: 64% !important;}

.tg_head_split,
.im_history_message_wrap.ng-scope,
.im_dialogs_modal_col_wrap .im_dialogs_search_field {
	background: transparent !important;
}
	/*.im_dialogs_col {background: rgba(0,0,0,0.5)}*/

.dropdown.open .tg_head_btn, .tg_head_btn:hover {
	background: none !important;
	box-shadow: inset 0 0 9px 4px rgba(255,255,255,1) !important;
	transition: all -1ms cubic-bezier(0.77, 0, 0.175, 1);
}

.dropdown.open .tg_head_btn, .tg_head_btn {
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}

.dropdown-menu {
	background: url(https://img-fotki.yandex.ru/get/6802/59844696.0/0_9cebb_719356a2_orig) !important;
	border: none !important;
}

.tg_head_logo_dropdown .dropdown-menu > li > a:hover,
	.tg_head_peer_dropdown .dropdown-menu > li > a:hover {
	background: none !important;
	box-shadow: inset 0 0 4px 2px rgba(255,255,255,1) !important;
	transition: all -.1s cubic-bezier(0.77, 0, 0.175, 1);
		font-size: 16px;
		color: #fff;
		text-shadow: -1px -1px 4px #fff;
}

.tg_head_logo_dropdown .dropdown-menu > li > a,
	.tg_head_peer_dropdown .dropdown-menu > li > a {
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
		font-size: 14px;
}

.im_message_author, .im_message_reply_author, .im_message_fwd_author {color: deepskyblue !important;}

.im_dialogs_search_field {
	background-color: rgba(0, 0, 0, 0) !important;
	border-color: blueviolet !important;
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}

.im_dialogs_search_field:active, .im_dialogs_search_field:focus {
    color: #fff !important;
	box-shadow: 0 0 9px 4px rgb(149, 108, 218) !important;
}

.im_dialog_peer {
	color: #fff
}

.im_dialogs_scrollable_wrap .active a.im_dialog {
	background-color: rgba(0, 146, 255, 0.5);
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
	margin-right: 0px;
}

.im_dialogs_scrollable_wrap a.im_dialog:hover, .im_dialogs_scrollable_wrap a.im_dialog_selected,
.contacts_modal_members_list .active a.contacts_modal_contact, .contacts_modal_members_list .active a.contacts_modal_contact:hover, .contacts_modal_members_list a.contacts_modal_contact:hover {
	background: rgba(182, 30, 224, 0.54);
	transition: all -.1s cubic-bezier(0.77, 0, 0.175, 1);
}

.im_dialogs_scrollable_wrap .active a.im_dialog:hover, .im_dialogs_scrollable_wrap .active a.im_dialog_selected {
    background-color: #0C8CEF;
}

.im_dialogs_scrollable_wrap a.im_dialog {
	transition: all 250ms cubic-bezier(0.77, 0, 0.175, 1);
}

a, .im_dialog_chat_from_wrap, .im_dialog_message_media, .im_dialog_message_service {
    color: #06C7F5;
}

a:focus, a:hover {
	color: #00FFF7
}

.composer_rich_textarea, .composer_textarea {
	box-shadow: none;
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
	background: rgba(0,0,0,0.5);
	font-size: 18px;
	line-height: 32px;
}

/*.composer_rich_textarea:focus, .composer_textarea:focus {
	box-shadow: 0 0 1px 1px rgb(0, 148, 255) !important;
}*/

a.composer_emoji_btn:hover {
	background: rgba(151, 110, 230, 0.5) !important;
}

.icon-tooltip-tail {
	background: rgb(10, 154, 243) !important;
	border: none !important;
}

.composer_emoji_insert_btn {
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}

.modal-content {
	background: url(https://img-fotki.yandex.ru/get/5103/59844696.0/0_9ceba_e7ad3885_orig);
}

.md_modal_head {
	background: url(https://img-fotki.yandex.ru/get/6836/59844696.0/0_9b726_2ea2b3ba_orig)
}

.form-control {
	background: transparent;
	border-color: blueviolet !important;
	transition: all 500ms cubic-bezier(0.77, 0, 0.175, 1);
}

.form-control:focus {
	color: #fff !important;
	box-shadow: 0 0 9px 4px rgb(149, 108, 218) !important;
}

.md_modal_list_peer_description .status_online, .md_modal_list_peer_description.status_online {
    color: #0BF315;
}
	/*левые границы сообщений*/
.im_history_selectable .im_message_outer_wrap:hover {
    background: none !important;
    border-left: 2px rgba(255, 255, 255, 0.5) solid !important;
		
}

.im_message_selected .im_message_outer_wrap, .im_message_focus .im_message_outer_wrap {
    background: none !important;
    border-left: 4px deepskyblue solid !important;
	  transition: border-color 500ms cubic-bezier(0.77, 0, 0.175, 1);
	  transition: border-width 100ms cubic-bezier(0.77, 0, 0.175, 1);
}

/*.im_message_reply_author.ng-scope {
    width: 340px !important;
}
*/

.md_modal_section_splitter {
	box-shadow: none !important;
	background: none !important;
}

.im_service_message {
	background: rgba(0, 0, 0, 0.75);
}

.im_message_unread_split {
	background: rgba(0, 146, 255, 0.7) none repeat scroll 0% 0%;
	color: rgb(255, 255, 255);
}

.im_history_col .nano > .nano-pane, .contacts_modal_col .nano > .nano-pane, .sessions_modal_col .nano > .nano-pane, .im_dialogs_modal_col .nano > .nano-pane {
	transition: all 1000ms cubic-bezier(0.77, 0, 0.175, 1) !important;
}
	
.composer_emoji_tooltip_shown {
		transform: scaleX(1) !important;
		box-shadow: 0 0 9px 4px rgb(10, 154, 243) !important;
	}
  
  .composer_emoji_tooltip {
    display: block !important;
    transition: all 300ms cubic-bezier(0.77, 0, 0.175, 1);
    background: url(https://img-fotki.yandex.ru/get/25541/59844696.0/0_a721b_4ef136c2_orig) !important;
	  box-shadow: none;
	  border: none !important;
    transform: scaleX(0);
	}	

.tg_head_main_wrap, .tg_head_logo_wrap {
	background: rgba(0,0,0,0.7)
}
	body.non_osx {
    font: 18px/18px segoe print;
	}
	
	.im_dialog_photo, .im_message_contact_photo, .im_message_from_photo, .im_message_fwd_photo, .im_panel_peer_photo, .im_panel_own_photo {
		border-radius: 0 !important;
	}
	
	.im_message_reply_body {
		line-height: 26px;
	}
	
	.im_send_reply_wrap {
	height: 42px;
	background: rgba(0, 0, 0, 0.5) none repeat scroll 0% 0%;
	}
	
	.is_2x .im_dialogs_search_field {
	background-image: none !important;
	}
	
	.im_history_typing a.im_history_typing_author, .im_history_typing {color: #00bfff !important;}
	.im_history_typing {background: rgba(0, 0, 0, 0.5);}

	.im_edit_forward_btn, .im_edit_reply_btn, .im_start_btn {
  color: #fff;
	border-radius: 5px !important;
  background: linear-gradient(180deg, rgba(100,100,100,1) 0, rgba(0,0,0,1) 100%) !important;
  transition: all 300ms cubic-bezier(0.42, 0, 0.58, 1);
}

.im_edit_forward_btn:hover, .im_edit_reply_btn:hover, .im_start_btn:hover {
  color: rgb(0, 191, 255);
  box-shadow: 0 0 4px 2px rgba(0,191,255,1) inset, -10px -6px 4px 0 rgba(90,90,90,1) ;
  text-shadow: -1px -1px 4px rgba(0,174,255,1) ;
  }

.im_edit_forward_btn:active, .im_edit_reply_btn:active, .im_start_btn:active {
  color: rgba(0,191,255,1);
  box-shadow: -5px -3px 2px 0 rgba(90,90,90,1) , 0 0 4px 2px rgba(0,191,255,1) inset;
  }
.im_edit_delete_btn {
color: #fff;
border-radius: 5px !important;
background: linear-gradient(180deg, rgba(100,100,100,1) 0, rgba(0,0,0,1) 100%) !important;
transition: all 300ms cubic-bezier(0.42, 0, 0.58, 1);
}
.im_edit_delete_btn:hover {
color: red;
box-shadow: 0 0 4px 2px red inset, -10px -6px 4px 0 rgba(90,90,90,1) ;
text-shadow: -1px -1px 4px red;
}
.im_edit_delete_btn:active {
color: red;
box-shadow: -5px -3px 2px 0 rgba(90,90,90,1) , 0 0 4px 2px red inset;
}
.btn-md, .btn-md-primary {
border-radius: 5px !important;
background: linear-gradient(180deg, rgba(100,100,100,1) 0, rgba(0,0,0,1) 100%) !important;
transition: all 300ms cubic-bezier(0.42, 0, 0.58, 1);
}
.btn-md:hover, .btn-md-primary:hover {
color: #fff;
box-shadow: 0 0 4px 2px #fff inset, -10px -6px 4px 0 rgba(90,90,90,1) ;
text-shadow: -1px -1px 4px #fff;
}
.btn-md:active, .btn-md-primary:active {
color: #fff;
box-shadow: -5px -3px 2px 0 rgba(90,90,90,1) , 0 0 4px 2px #fff inset;
}

	.im_submit {
		height: 30px !important;
		padding: 6px 10px !important;
	}
	
.tg_head_peer_dropdown .dropdown-menu, .tg_head_logo_dropdown.open .dropdown-menu {
	animation: 0.2s ease-in 0s normal none 1 running rollIn;
	z-index: 4;
	}	

	.im_message_date {opacity: 0.3}
	.im_message_date:hover, .im_message_fwd_date:hover {
		color: #fff;
		opacity: 1;
		transition: all 300ms cubic-bezier(0.42, 0, 0.58, 1);
	}
  .im_content_message_wrap.im_message_out {
	margin-left: 51%;
}
	.im_content_message_wrap.im_message_in {
	margin-right: 51%;
}
	.im_message_out .im_message_from_photo, .im_message_out .im_message_author {
	display: none !important;
}
	.im_message_in .im_message_text {
	border: 2px deepskyblue solid !important;
	border-radius: 5px !important;
	padding: 5px !important;
}
	.im_message_out .im_message_text {
	border: 2px blueviolet solid !important;
	border-radius: 5px !important;
	padding: 5px !important;
}
}
