css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);
body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("http://blog.roblox.com/wp-content/uploads/2013/08/Stratosphere-Settlement-by-EBR.jpg") !important;
    background-image: url("http://puu.sh/hVu0O/84f281834d.png") !important; /*custom banner*/
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(5px) !important;
	-webkit-filter: blur(5px) !important;
    z-index: -1 !important;
  }
  .rbx-header {
background-color: rgba(32, 73, 70, 0.74) !important;
}
  .section {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
  .game .game-item {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
/*Trim content backgrounds, fill in new ones*/
  #Body, #BodyWrapper {
    background: transparent !important;
  }
  #mid-column {
    background-color: #FFF !important;
  }
  .BuildPageContent {
    background-color: #FFF !important;
    float: none !important;
  }
  #MyCreationsTab {
    width:792px !important;
  }
  #AccountPageRight {
    background-color: #FFF !important;
  }
  .settings-section {
    padding: 10px !important;
  }
  .divider-right {
    border: none !important;
  }
  .StandardPanelContainer .StandardPanelWhite {
    padding: 0 !important;
    padding-left: 6px !important;
  }
  #GamesPageRightColumnSidebar {
    background-color: #FFF !important;
    padding-top: 20px !important;
  }
  .sidebar-no-ad {
    top: 72px !important;
  }
  .games-list-container {
    border-right: 5px solid #FFF !important;
    border-left: 5px solid #FFF !important;
  }
  #DivToHideOverflowFromLastGamesList { /*lol*/
    background: transparent !important;
  }
  div#catalog {
    background-color: #FFF !important;
    padding-left: 10px !important;
    padding-bottom: 5px !important;
  }
  .right-content {
    padding-left: 10px !important;
  }
  #UserContainer {
    background-color: #FFF !important;
    padding-left: 5px !important;
  }
  .divider-right, .divider-left {
    background-color: #FFF !important;
  }
  #ctl00_cphRoblox_rbxHeaderPane_statusBox {
    background-color: #FFF !important;
  }
  .game-main-content .game-calls-to-action .game-name {
    background: transparent !important;
  }
  .game-main-content .game-calls-to-action .game-name::after {
    background: transparent !important;
  }
  #Item {
    background-color: #FFF !important;
    padding-top: 5px !important;
    padding-right: 5px !important;
  }
  .rbx-left-col {
  background: rgba(5, 36, 25, 0.27)
  }

.rbx-left-col li a{
    color: #FFFFFF
}
div.rbx-left-col a:not(:hover) span[class^="rbx-icon"], .rbx-scrollbar li a:not(:hover) span[class^="btr_icon"] {
	-webkit-filter: invert(100%);
	filter:invert(100%);
}
.rbx-text-notes{
    color: #FFFFFF
}
