@-moz-document domain(upwork.com) {
@-moz-document domain(upwork.com) {
/*------------------------------------*
    Font
\*------------------------------------*/
* {
  font-weight: normal !important;
  font-family: Lao UI, "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}
body,
body p {
  font-size: 14px;
  font-weight: normal;
  font-style: normal;
  font-family: Lao UI, "Helvetica Neue", Helvetica, Arial, sans-serif;
}
  
  
   .text-primary { color: #333; }
  .oBtnPrimary, .oBtnVisitorPrimary { background: #009FF0; }
  .oHeader .oTwizzlr .oNavTab.isCurrent { color: #009FF0; }
  .oHeader .oTwizzlr .oNavTab { font-family: Lao UI, Arial, sans-serif; font-size: 18px; font-weight: bold; }
  .oHeader .oTwizzlr .oSecondaryNavList a { font-family: Lao-ui,Arial, sans-serif; font-size: 16px;}

  /* Search Result Job Title */
  body { font-family: Khmer UI, Arial, sans-serif; }



  /* Search Result Job Title */
  a, .oBtnTertiary, .oBtnCancel, .oBtnPopup, .oThumbList figcaption, .oMoreSmall, .oLink { color: #009FF0; font-family: Khmer UI, Arial, sans-serif; }



  /* Counter */
  .oListLite { counter-reset: section; }
  .oJobTile:before {
    color: #333333;
    content: counter(section, decimal) ". ";
    counter-increment: section;
    float: left;
    font-size: 45px;
    height: 59px;
    margin: 24px 0;
    text-align: center;
    text-shadow: 1px 1px 1px #FFFFFF;
    width: 70px;
  }

  
.oTxtSmall, .oPwdErr {
    font-size: 13px !important;
  font-family: Lao UI, "Helvetica Neue",Helvetica,Arial,sans-serif !important;
    font-weight: normal !important;
}
  
.oSecondaryNavList a {
    font-family: Khmer UI,Arial,sans-serif;
    font-variant-caps: all-small-caps;
    font-weight: bold;
}    
.oHeader .oTwizzlr .oSecondaryNavList a {
    font-family: Khmer UI !important;
    font-size: 15px !important;
}  
.oHeader .oTwizzlr .oNavTab:hover {
    color: #009ff0;
}
  
#lia-body .lia-component-custom-user-navigation-links .custom-user-options .custom-user-options-option a {
    color: #57a;
    font-family: Khmer UI;
  font-size: 15px;}
 
row-footer ul li a {
    color: #557;
    font-family: Lao UI;
    font-size: 14px;
}  
community-node-tree-item {
    color: #57a;
    font-family: Khmer UI;
    font-size: 14px;
    font-weight: normal !important;
  line-height: 14px;}  
  
  .lia-link-navigation {
    color: #469 !important;
    font-family: Lao UI;
    font-size: 15px;
    font-weight: bold;
    line-height: 40px;
    text-transform: capitalize;
}
/**
 * Bold
 */
b,
strong,
legend,
label,
h3,
h4,
h5,
h6,
.oH3,
.oH4,
.oH5,
.oH6,
h3 .ng-binding,
body.oContractor .oSide .oTxtExtra.ib,
.font-gotham-medium,
.btn,
.body-content strong,
.accordion .title a,
.font-gotham-bold,
.logo,
.body-content a,
.accordion .content h4,
.oRowTitle,
.oRowTitle a,
.oScore,
.oBreadcrumbBack,
.oSpendIndicator,
.oSegmentedControl.oSimple li .isActive,
.oDataTable .oGrandTotal,
.oTable th,
.oTablePastHires th,
.oNewRateTable th,
.oReportTable th,
.oMessageThread .isMe,
.oMessageThread .isOther1,
.oMessageThread .isOther2,
.oMessageThread .isOther3,
.oMessageThread .isOther4,
.oOptLbl,
.oLeftNav a.isCurrent,
.oLeftNav .isCurrent.oBtnTertiary,
.oLeftNav .isCurrent.oBtnCancel,
.oLeftNav .isCurrent.oBtnPopup,
.oLeftNav .oThumbList figcaption.isCurrent,
.oThumbList .oLeftNav figcaption.isCurrent,
.oLeftNav .isCurrent.oMoreSmall,
.oHorizForm a,
.oFilterBar a,
.oHorizForm .oBtnTertiary,
.oFilterBar .oBtnTertiary,
.oHorizForm .oBtnCancel,
.oFilterBar .oBtnCancel,
.oHorizForm .oBtnPopup,
.oFilterBar .oBtnPopup,
.oHorizForm .oThumbList figcaption,
.oThumbList .oHorizForm figcaption,
.oFilterBar .oThumbList figcaption,
.oThumbList .oFilterBar figcaption,
.oHorizForm .oMoreSmall,
.oFilterBar .oMoreSmall,
.oTransitionalHeader a,
.oTransitionalHeader .oBtnTertiary,
.oTransitionalHeader .oBtnCancel,
.oTransitionalHeader .oBtnPopup,
.oTransitionalHeader .oThumbList figcaption,
.oThumbList .oTransitionalHeader figcaption,
.oTransitionalHeader .oMoreSmall,
.oWidget>footer>a,
.oWidget>footer>.oBtnTertiary,
.oWidget>footer>.oBtnCancel,
.oWidget>footer>.oBtnPopup,
.oThumbList .oWidget>footer>figcaption,
.oWidget>footer>.oMoreSmall {
  font-weight: bold !important;
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}
/*------------------------------------*
    Icons
\*------------------------------------*/
.caret,
.checkbox input[type=checkbox]:checked+.checkbox-replacement-helper:before,
.checkbox-inline input[type=checkbox]:checked+.checkbox-replacement-helper:before,
.glyphicon,
.glyphicon-md,
.modal-header .close,
[class*=" air-icon-"],
[class*=" o-icon-"],
[class^=air-icon-],
[class^=o-icon-] {
  font-family: 'Air icons 20' !important;
  font-weight: 400 !important;
}
.glyphicon-lg {
  font-family: 'Air icons 40' !important;
}
/*------------------------------------*
    Link color
\*------------------------------------*/
a,
.btn-link,
.bid-range small,
.oLinkButton,
.oMore,
.oMoreLarge,
.oBtnTertiary,
.oBtnCancel,
.oBtnPopup,
.oThumbList figcaption,
.oMoreSmall,
.oLink {
  color: #1d90f8;
}
a:hover,
.btn-link:hover,
.bid-range small:hover,
.oLinkButton:hover,
.oMore:hover,
.oMoreLarge:hover,
.oBtnTertiary:hover,
.oBtnCancel:hover,
.oBtnPopup:hover,
.oThumbList figcaption:hover,
.oMoreSmall:hover,
.oLink:hover,
a:focus,
.btn-link:focus,
.bid-range small:focus,
.oLinkButton:focus,
.oMore:focus,
.oMoreLarge:focus,
.oBtnTertiary:focus,
.oBtnCancel:focus,
.oBtnPopup:focus,
.oThumbList figcaption:focus,
.oMoreSmall:focus,
.oLink:focus {
  color: #066dca;
}
.oLinkButton,
.oMore,
.oMoreLarge {
  color: #1d90f8 !important;
}
.oLinkButton:hover,
.oMore:hover,
.oMoreLarge:hover,
.oLinkButton:focus,
.oMore:focus,
.oMoreLarge:focus {
  color: #066dca !important;
}
.oWidget a,
.oTopicsNavigation.oLeftNav li.isCurrent .oTopic,
.oTab,
.oPlainList a,
a.o-tag-skill,
#jsFlagJobLink {
  color: #494949;
}
/**
 * Visited
 */
.oRowTitle.oH3 a:visited,
.jsShortName:visited {
  color: #870ec3;
}
/**
 * Pagination
 */
.oPager.isCurrent,
.oPager:hover {
  color: #1d90f8;
  border-color: #1d90f8;
  box-shadow: inset 0 0 0 1px #1d90f8;
}
/**
 * oSideSection oStarsSmall
 */
.oSideSection .oStarsContainer.oStarsSmall,
.oSideSection .oStarsContainer.oStarsSmall>.oStarsValue {
  background-size: auto 0.7em;
}
/*------------------------------------*
    Footer (too much space)
\*------------------------------------*/
Link color,
.oFooter .oPlainList > li > a {
  color: #999;
}
.oPortraitMedium > img {
  border-radius: 5px;
}
.oFooter {
  padding: 50px 0;
}
.oFooter .oSocialIconsBox {
  padding: 50px 0;
}
/*------------------------------------*
    Avatar (Find Work page)
\*------------------------------------*/
.oPortraitMedium > img {
  border-radius: 5px;
}
/*------------------------------------*
    community.upwork.com
\*------------------------------------*/
.PostPage .lia-form-subject-input,
.PostPage .lia-form-tags-input.lia-form-type-text.lia-form-input-vertical,
.ReplyPage .lia-form-subject-input,
.ReplyPage .lia-form-tags-input.lia-form-type-text.lia-form-input-vertical,
.EditPage .lia-form-subject-input,
.EditPage .lia-form-tags-input.lia-form-type-text.lia-form-input-vertical,
#lia-body input[id^="messageSearchField"] {
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}
#lia-body .KudosButton .lia-button-image-kudos-count .lia-link-navigation.kudos-count-link span,
#lia-body .lia-content .UserName a span,
#lia-body .lia-content .lia-user-name a span,
#lia-body.ForumPage #messageList table.lia-list-wide td .js-latest-post-by-from a.lia-user-name-link span,
#lia-body.BlogPage .lia-content .lia-message-body a.read-more-link,
#lia-body.IdeaExchangePage .lia-quilt-column-main-content .lia-message-view .lia-component-message-header .lia-message-author .UserName.lia-user-name a,
#lia-body .lia-component-forums-widget-posttabs td.messageSubjectColumn .MessageSubject a,
#lia-body.PostPage .InfoMessage.lia-panel-feedback-inline-note.lia-component-common-widget-feedback .lia-text p,
.ReplyPage .InfoMessage.lia-panel-feedback-inline-note.lia-component-common-widget-feedback .lia-text p,
.EditPage .InfoMessage.lia-panel-feedback-inline-note.lia-component-common-widget-feedback .lia-text p,
#lia-body .MessageEditor .message-editor-controls a.quote-message-link,
#lia-body.PostPage .lia-form-fieldset-wrapper.lia-form-attachments-fieldset-toggle.form-fieldset-last fieldset legend,
.ReplyPage .lia-form-fieldset-wrapper.lia-form-attachments-fieldset-toggle.form-fieldset-last fieldset legend,
.EditPage .lia-form-fieldset-wrapper.lia-form-attachments-fieldset-toggle.form-fieldset-last fieldset legend,
#lia-body .lia-button.lia-button-primary.lia-button-Submit-action,
#lia-body .lia-button.lia-button-primary.lia-button-Cancel-action,
#lia-body .lia-content legend,
#lia-body .lia-content .lia-component-common-widget-breadcrumb ul.lia-list-standard-inline li.final-crumb span,
#lia-body.ForumPage #messageList table.lia-list-wide td.threadSubjectColumn .MessageSubjectCell .message-subject a,
#lia-body.BlogPage .lia-content .lia-panel-message-root.lia-message-blog.lia-message-read .lia-message-heading .lia-message-subject a,
#lia-body.IdeaExchangePage .lia-content .lia-panel-message-root.lia-message-idea .lia-message-heading h2 a,
.ViewProfilePage .lia-content table.lia-list-statistics tr .lia-data-cell-primary,
.ViewProfilePage .lia-panel.lia-panel-standard.MessageListTaplet .MessageSubject .message-subject a,
.ViewProfilePage .lia-component-view-profile-banner .lia-user-rank,
.ViewProfilePage .lia-content table.lia-list-statistics tr .lia-data-cell-secondary,
.ViewProfilePage .lia-panel.lia-panel-standard.MessageListTaplet .MessageSubject .message-subject-board a {
  font-weight: bold !important;
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}
#lia-body.ForumTopicPage .lia-quilt-column-main-content .message-list .lia-panel-message .lia-quilt-column-main-right .lia-message-body .lia-message-body-content p,
#lia-body.ForumTopicPage .lia-quilt-column-main-content .message-list .lia-panel-message .lia-quilt-column-main-right .lia-message-body div.lia-message-body-content {
  font-size: 14px;
}
.mce-btn-small .mce-ico {
  font-family: tinymce-small, Arial !important;
}
/**
 * Avatar (fix distortion)
 */
.UserAvatar.lia-link-navigation.lia-link-disabled,
.UserAvatar.lia-link-navigation.lia-avatar-editable,
#lia-body.ForumTopicPage .lia-content .lia-component-message-list a.UserAvatar.lia-link-navigation {
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: center;
  -webkit-justify-content: center;
      -ms-flex-pack: center;
          justify-content: center;
  border-radius: 50%;
  border: 2px solid #b2b2b2;
  overflow: hidden;
  -webkit-animation-duration: 0.8s;
          animation-duration: 0.8s;
  -webkit-animation-fill-mode: both;
          animation-fill-mode: both;
}
.UserAvatar.lia-link-navigation.lia-link-disabled,
.UserAvatar.lia-link-navigation.lia-avatar-editable {
  width: 100px;
  height: 100px;
}
#lia-body.ForumTopicPage .lia-content .lia-component-message-list a.UserAvatar.lia-link-navigation {
  width: 64px;
  height: 64px;
}
#lia-body.ForumTopicPage .lia-content .lia-component-message-list a.UserAvatar.lia-link-navigation:hover {
  border-color: #5bbc2e !important;
  -webkit-animation-name: tada;
          animation-name: tada;
}
#lia-body .lia-content .lia-user-avatar-profile,
#lia-body.ForumTopicPage .lia-content .lia-component-message-list .lia-user-avatar-message {
  width: auto !important;
  height: auto !important;
  border-radius: 0 !important;
  border: 0 !important;
  -webkit-box-flex: 1;
  -webkit-flex: 1 0 auto;
      -ms-flex: 1 0 auto;
          flex: 1 0 auto;
}
/*------------------------------------*
    Animate
\*------------------------------------*/
@-webkit-keyframes tada {
  0% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
  10%, 20% {
    -webkit-transform: scale3d(0.9, 0.9, 0.9) rotate3d(0, 0, 1, -3deg);
    transform: scale3d(0.9, 0.9, 0.9) rotate3d(0, 0, 1, -3deg);
  }
  30%, 50%, 70%, 90% {
    -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
    transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  }
  40%, 60%, 80% {
    -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
    transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  }
  100% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}
@keyframes tada {
  0% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
  10%, 20% {
    -webkit-transform: scale3d(0.9, 0.9, 0.9) rotate3d(0, 0, 1, -3deg);
    transform: scale3d(0.9, 0.9, 0.9) rotate3d(0, 0, 1, -3deg);
  }
  30%, 50%, 70%, 90% {
    -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
    transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, 3deg);
  }
  40%, 60%, 80% {
    -webkit-transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
    transform: scale3d(1.1, 1.1, 1.1) rotate3d(0, 0, 1, -3deg);
  }
  100% {
    -webkit-transform: scale3d(1, 1, 1);
    transform: scale3d(1, 1, 1);
  }
}

}

}
