css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('dictionary.goo.ne.jp'),
	domain('wpedia.goo.ne.jp') {


/* --*--*--*--*--*--*--*--*--*--*--
	ä¸è¦ãªé …ã‚’éžè¡¨ç¤º
--*--*--*--*--*--*--*--*--*--*-- */

#rside,
[class~='bookmark'],
br.clearboth,
div#footer,
div#header > ul#logo,
div#header > center,
div#header > div#headnavi,
div#incontents > .clearboth,
div#incontents > table,
div#incontents > div.adlink,
div#incontents div[class~='sbm'],
div#incontents div.websearch,
div#incontents h1.ch04,
div#incontents > .wordDefinition + br + div,
div#incontents > .wordDefinition + br + div + div,
div#incontents > .wordDefinition + br + div + div + div,
div#incontents > .wordDefinition + br + div + div + div + div,
div.hint ~ div[class^='tab_'],
div.lbox > dl tr + tr + tr + tr + tr ~ tr,
div.rbox ~ div[class^='tab_'],
div.rbox > table:last-child,
dl.ranking > dt + dd[class],
dl.ranking > dd[class~='center'] + dd[class~='center'],
input[name='web'] {
	display: none !important;
}

/* --*--*--*--*--*--*--*--*--*--*--
	æ•´å½¢
--*--*--*--*--*--*--*--*--*--*-- */

* html,
body {
	margin-left: auto !important;
	margin-right: auto !important;
	min-width: 100px !important;
	padding: 0px !important;
	width: 576px;
	overflow-x: hidden;
}

.idiom {
	z-index: 100 !important;
}

div#header > *,
div#footer > *,
div#incontents,
div#incontents > h2.ch04,
div#incontents > div[class^='result'],
div#incontents > div[class*='Result'],
div#incontents > div[id^='eijiro'] {
	width: 576px !important;
}

form[name='dict'] select[name='search_history'] {
	width: 120px !important;
}
form[name='dict'] input[type='text'] {
	width: 240px !important;
}

div.rbox {
	margin: 0px !important;
}

div[class~='past'] {
	margin-bottom: 0px !important;
}

}
