css:
@-moz-document domain("pr0gramm.com") {
html, body {
	background: #ffffff;
	color: #111111;
}

#head-content {
      background: rgba(250,250,250,0.95);
}

input {
	background-color: whitesmoke;
    color: black;
	border-color: gray;
	border-width: 1px;
	border-style: solid;
}

input, textarea {
	background-color: whitesmoke;
    color: black;
	border-color: gray;
	border-width: 1px;
	border-style: solid;
}

input :focus {
	background-color: white;
}

input[type=text] {
    color: black;
}

input:focus, textarea:focus {
	background-color: white;
	color: black;
}

input[type=password] {
    color: black;
}

div.item-container {
	background: #f5f5f5;
}

element.style {
	color: black !important;
}

.head-link {
	color: black;
}

#inboxLink.empty {
	color: black;
}

a.head-tab {
	color: black;
}

.confirm-button, input[type=button], input[type=submit] {
	color: black;
}

input.q {
	background-color: white;
}

.confirm-button:hover, input[type=button]:hover, input[type=button]:focus, input[type=submit]:hover, input[type=submit]:focus {
	background-color: gray;
}

textarea.comment{
	background-color: white;
    color: black;
}

a, .link {
	color: #ee4d2e;
}

a:hover, .link:hover, .action:hover {
	color: gray;
}

a.tag-link:hover, span.tag.voted-down a.tag-link:hover {
	color: #ee4d2e;
}

.time {
  color: gray;
}

a.time:hover {
    color: #ee4d2e;
}

.user {
	color: #ee4d2e;
}

a.user:hover {
	color: #161618;
}

.vote-down:hover, .voted-up .vote-down:hover, .voted-down .vote-down:hover {
	color: #161618;
}

.um0:after {
	color: #00cd00;
}

.user:after {
	font-size: medium;
}

.um1:after {
	color: #f245f9;
}

.um6:after {
    color: #a36541;
}

#footer-links {
	background-color: white;  
}

#footer-links a:hover {
	color: #ee4d2e;
}

h3 {
	color: #161618;
}

div#overlay-box {
	background-color: rgba(250,250,250,0.9);
}

.faint-footer a {
  color: #ee4d2e;
}

.faint-footer a :hover {
  color: green;
}

div.product-description {
	background-color: lightgray;
}

.user-score {
  color: #ee4d2e;
}

.badges {
	background-color: lightgray;
    border-radius: 10px;
}

#pr0gramm-logo {
  -webkit-filter: invert(1) hue-rotate(180deg) contrast(1) brightness(0.5) saturate(3);
}

img.badge {
  -webkit-filter: invert(1) hue-rotate(180deg) contrast(1) brightness(0.5) saturate(3);
}

span.badge-extra {
    color: whitesmoke;
}
}
