css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  body {
    background: url("http://fc01.deviantart.net/images3/i/2004/099/3/3/Mr__Mime_Under_Glass_.png") !important;
    background-size: cover !important;
  }
  
   #header {
    background: url("http://i.imgur.com/XkjakIM.png") 
  }
  
  .userbar .username {
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
  }
  /*main background layout */
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(0, 0, 0, 0.3);
    border-color: #000;
  }
  
  .mainmenuwrapper {
    background: none !important;
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0.2);
  }
  
  .label {
    text-shadow: none;
    color: #000;
  }
 
  .menugroup .button {
    border: 1px solid #101010;
    color: #FFF;
    text-shadow: 0px 0px 1px #000;
    background: rgba(44, 44, 44, 0.8);
  }
 
  .menugroup .button:hover {
    border: 1px solid #101010;
    color: #000;
    text-shadow: none;
    background: rgba(44, 44, 44, 0.8);
  }
  
  .roomlist a.ilink {
    border: 1px solid #101010;
    color: #FFF;
    text-shadow: 0px 0px 1px #000;
    background: rgba(44, 44, 44, 0.8);
  }
  
  .roomlist a.ilink small {
