    text-shadow: 0px 0px 1px #000;
  }
  
  .roomlist a.ilink:hover {
    border: 1px solid #101010;
    color: #000;
    text-shadow: none;
    background: rgba(44, 44, 44, 0.8);
  }
 
  .maintabbarbottom {
    background:#000;
    border:1px solid #000;
  }
 
  .tabbar a.button.notifying {
