css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("search.disconnect.me") {
  
body {
  font-family: arial,sans-serif !important;
}

div#sidebar, #premium-protection {display: none!important;}
  
body div#results ul#normal-results li {
  line-height: 1.2 !important;
}

body div#results ul#normal-results li a.title {
  font-size: 18px !important;
  text-decoration: none !important;
}
  
body div#results ul#normal-results li a.title:hover {
  text-decoration: underline !important;
}
  
body div#results ul#normal-results li a.title:visited {
  text-decoration: underline !important;
  color: #551A8B !important;
}
  
body div#results ul#normal-results li p.url {
  font-size: 14px !important;
}
  
body div#results ul#normal-results li p:nth-child(3) {
  line-height: 1.3 !important;
  font-size: 1em !important;
}
  
body div#results ul#normal-results li:not(.image) {
  width: 650px !important;
}
  
span.spell {
  color: #1A0DAB !important;
}
  
span.protected a {
  color: #777 !important;
}
  
span.protected a:hover {
  color: #000 !important;
}
  
span.hdtb_msel {
  border-bottom: 3px solid #DD4B39 !important;
}
  
span.hdtb_msel a, span.hdtb_msel a:hover {
    color: #DD4B39 !important;
}
  
span.hdtb_mitem div {
  font-weight: bold !important;
}

/* Search bar section*/
  
div#search_provider_js {
  float: right !important;
}
  
header fieldset #create {
  background-color: #4A8BF5 !important;
}
 
/*Footer*/
  
.pagination .text-link {
  text-decoration: none !important;
}
  
.pagination .text-link:hover {
  text-decoration: underline !important;
} 
}
