css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("search.disconnect.me") {
  
/* Colour */

body, div#results #normal-results.image-results {
  background: #111 !important;
}
  
div.top-form-input {
  border: none !important;
}
  
input.searching {
  background-color: #444 !important;
  height: 36px !important;
}
  
div.menu_results {
  background: #222 !important;
  border-bottom: 1px solid #555 !important;
}
  
span.bar {
  border-left: 2px solid #555 !important;
}
  
span.hdtb_mitem a {
  border: none !important;
}
  
header {
  background: #333 !important;
  border-bottom: none !important;
}
  
body div#results ul#normal-results li a.title {
  color: #bbb !important;
}
  
body div#results ul#normal-results li a.title:visited {
  color: #caa !important;
  text-decoration: underlined !important;
}
  
body div#results ul#normal-results li p:nth-child(3) {
  color: #999 !important;
}
  
.dropdown_wrapper .dropdown_content ul {
  background-color: #222 !important;
}
  
.dropdown_wrapper .dropdown_content ul li {
  border: 1px solid #777 !important;
}
  
div.pagination, div.pagination a {
  color: #aaa !important;
}
  
input.search_box {
  background-color: #aaa !important;
  border: none !important;
}
  
header fieldset #create {
  height: 36px !important;
}
  
header fieldset #create:focus {
  box-shadow: none !important;
}
  
select#location {
  border: none !important;
  background-color: #222222 !important;
  color: #ddd !important;
}
  
ul.ui-autocomplete {
  background-color: #aaa !important;
  border: none !important;
  border-top: 1px solid #888 !important;
}
  
input.search_box:focus {
  box-shadow: none !important;
}
  
a.ui-state-focus {
  background-color: #777 !important;
}
  
header fieldset #create {
  background-color: #777 !important;
}
 
/* Main */

div#sidebar, #premium-protection {display: none!important;}
  
body {
  font-family: arial,sans-serif !important;
}
  
body div#results ul#normal-results li {
  line-height: 1.2 !important;
}

body div#results ul#normal-results li a.title {
  font-size: 18px !important;
  text-decoration: none !important;
}
  
body div#results ul#normal-results li a.title:hover {
  text-decoration: underline !important;
}
  
body div#results ul#normal-results li p.url {
  font-size: 14px !important;
}
  
body div#results ul#normal-results li p:nth-child(3) {
  line-height: 1.3 !important;
  font-size: 1em !important;
}
  
body div#results ul#normal-results li:not(.image) {
  width: 650px !important;
}
  
span.spell {
  color: #1A0DAB !important;
}
  
span.protected a {
  color: #777 !important;
}
  
span.protected a:hover {
  color: #000 !important;
}
  
span.hdtb_msel {
  border-bottom: 3px solid #B13C2E !important;
}
  
span.hdtb_msel a, span.hdtb_msel a:hover {
    color: #B13C2E !important;
}
  
span.hdtb_mitem div {
  font-weight: bold !important;
}

/* Search bar section*/
  
div#search_provider_js {
  float: right !important;
}
 
/*Footer*/
  
.pagination .text-link {
  text-decoration: none !important;
}
  
.pagination .text-link:hover {
  text-decoration: underline !important;
} 
}
