/***************************	translate.google.com	******************************/

@-moz-document url-prefix("http://translate.google."), url-prefix("https://translate.google.") {
/* backgrounds */
 
#navlink, 
#gt-appbar {
	background-color: #000000 !important; 
}  

/* header */
.gb_5b {
	background: #000000 !important;
}

#gb#gb a.gb_la {
	color: #167AC6 !important;
}

/*** applications ***/

.gb_ba {
	border-radius: 4px !important;
	border: 1px solid #ffffff !important;
	background: #000000 !important;
}

.gb_Fa,
.gb_Ia  {
	background: #000000 !important;
}

/* colours footer */
#gb a.gb_Fa,
#gb a.gb_Ja {
    color: #167AC6 !important;
}

.gb_ra {
    border: 0px solid rgba(255, 255, 255, 0)!important;
}

#gb#gb a.gb_ka:hover {
    border: 0px solid rgba(255, 255, 255, 0)!important;
}

#gb#gb a.gb_ka, #gb#gb a.gb_la {
    color: #167AC6 !important ;
}

.gb_ra:hover .gb_ka,
.gb_ra:hover .gb_wa {
	background: #3d3d3d !important; 
}

/* login button */
.gb_b {
	color: #ffffff !important;
	border-radius: 4px !important;
	border: 1px solid #141414 !important;
	background-image: linear-gradient(#3d3d3d, #2d2d2d, #1a1a1a) !important; 
	margin: 0px !important;
	margin-top: 0px !important;
	margin-bottom: 1px !important;
	-moz-background-clip: padding !important; 
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

.gb_b:hover {
	color: #ffffff !important;
	background: -moz-linear-gradient(to bottom, #00cfeb, #006471, #006471) !important;
	border: 1px solid #141414 !important;    
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

/* change icons on top */
.gb_Ea .gb_ga, /* button showing applications */
.gb_hb.gb_ib ,
.gb_1b .gb_ga {
	opacity: 1.0 !important; 
	-webkit-filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
	filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
}

/* body */
#gt-apb-main {
	background-color: #000000 !important;
}

body {
	background-color: #333230 !important;
	color: white !important; 
}

/* interior */
#gt-text-c {
	background-color: #333230 !important;
	border: #ffffff !important;
} 

#gt-appbar #gt-appname {
	color: #167AC6 !important;
}

/* text area left  */
#source {
	background-color: #000000 !important;
	color: dodgerblue !important;
} 

#gt-src-wrap {
	border: 1px solid #ffffff;
	background-color: #000000!important;
}

.gt-hl-layer {
	background:  transparent !important;
	border: transparent !important;
}

/* text area right */
#result_box {
	background-color: #000000 !important;
	color: dodgerblue !important;
} 

#gt-res-wrap {
	border: 1px solid #ffffff;
	background-color: #000000 !important;
}

/* phrases dictionary */
#gt-pb-tt, #gt-pb-pt {
	color: #167AC6 !important;
}

#gt-pb-tb  {
	color: #000000 !important;
}

#gt-pb-tb tr {
	color: #000000 !important;
}

#gt-pb-tb tr:hover {
	color: #000000 !important;
	background-color: #167AC6 !important; 
}

#pb-tool {
	color: #ffffff !important;
	background-color: #333230 !important; 
}

#pb-tb-c {
	background-color: #333230 !important;
}

/* buttons */
.jfk-button-action,
.jfk-button-standard {
	/*border-color: #ffffff !important;*/
}

/* colours left side */
.gt-def-example {
	color: #ffffff !important; /* white */
}

.gt-spell-correct-message {
	color: #239B2D !important; /* green */
}

.translit {
	color: #ffffff !important ; /* white */
}

.gt-ex-text,
.gt-def-row {
	color: #D7A61B !important; /* orange */
}

.gt-cd-t {
	color: #239B2D !important ; /* green */
}

.gt-cd-pos {
	color: #FCF4F4 !important; /* light white */
	font-style: italic !important;
}

.gt-cd-cl,
.gt-card-ttl-txt,
.gt-ct-text {
    color: #167AC6 !important; /* light blue */
}

/* colours right side  */
.gt-card-ttl-txt,
.gt-rw-span,
.gt-baf-word-clickable {
	color: #167AC6 !important;
}

.gt-baf-back:hover {
	color: #167AC6 !important;
}

/* change colour icons */
.cd-expand-button,
.trans-pb-button .jfk-button-img,
.gt-hats-icon, 
span.check, 
input[type="radio"], 
.sm-arrow,
.modal-dialog-title-close,
.jfk-checkbox, 
.jfk-button-img,
.goog-flat-menu-button-dropdown, 
.goog-toolbar-menu-button-dropdown,
.ita-kd-img,
.ita-kd-arrow,
/*  .ita-kd-checkbox, */
.ita-ppe-pgd, 
.ita-ppe-pgu,  
.vk-t-btn.vk-sf-cl {
	opacity: 1.0 !important; 
	-webkit-filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
	filter: invert(100%) hue-rotate(130deg) contrast(500%) saturate(300%) brightness(200%);
} 

.ita-kd-inputtools-div .ita-kd-icon-button,
.goog-toolbar-button, .goog-toolbar-menu-button {
	background: transparent  !important;
	color: white !important; 
} 

/* languages buttons */
#gt-sl, 
#gt-tl, 
#anno1, 
#anno2, 
#clp-btn,
#gt-swap, 
#gt-sl-gms, 
#gt-tl-gms,
.ita-hwt-backspace, 
.ita-hwt-space, 
.ita-hwt-enter, 
.jfk-button-action, 
.jfk-button-standard, 
.jfk-button-primary,
.goog-buttonset-default, 
.modal-dialog-buttons button {
	color: #ffffff !important;
	border-radius: 4px !important;
	border: 1px solid #141414 !important;
	background-image: linear-gradient(#3d3d3d, #2d2d2d, #1a1a1a) !important; 
	margin: 0px !important;
	margin-top: 0px !important;
	margin-bottom: 1px !important;
	moz-background-clip: padding !important; 
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

#gt-sl:hover, 
#gt-tl:hover, 
#anno1:hover, 
#anno2:hover, 
#clp-btn:hover,
#gt-swap:hover, 
#gt-sl-gms:hover, 
#gt-tl-gms:hover,
.ita-hwt-backspace:hover, 
.ita-hwt-space:hover, 
.ita-hwt-enter:hover, 
.jfk-button-action:hover, 
.jfk-button-standard:hover, 
.jfk-button-primary:hover,
.goog-buttonset-default:hover, 
.modal-dialog-buttons button:hover {
	color: #ffffff !important;
	background: -moz-linear-gradient(to bottom, #00cfeb, #006471, #006471) !important;
	border: 1px solid #141414 !important;    
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

.cd-expand-button {
	color: black !important; 
}

#gt-pb-sbt {
	background: #167AC6 !important;
}

.ita-kd-inputtools-div {
	background-color: rgba(255, 255, 255, 0.0) !important;
}

/* checked buttons status */
.jfk-button-checked {
	background-color: #167AC6 !important;
	background-image: none !important;
}

/* keyboard */
.vk-box {
	background: #000000 !important;
	color: white!important; 
	border-color: #ffffff !important;
	border-width: 1px  !important;
	border-radius: 4px !important;
}

.vk-btn {
	color: #ffffff !important;
	border-radius: 4px !important;
	border: 1px solid #141414 !important;
	background-image: linear-gradient(#3d3d3d, #2d2d2d, #1a1a1a) !important; 
	margin: 0px !important;
	margin-top: 0px !important;
	margin-bottom: 1px !important;
	-moz-background-clip: padding !important; 
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

.vk-btn:hover {
	color: #ffffff !important;
	background: -moz-linear-gradient(to bottom, #00cfeb, #006471, #006471) !important;
	border: 1px solid #141414 !important;    
	text-shadow: 0px -1px rgba(0, 0, 0, 0.51153) !important;
}

/* text on top keyboard */
.vk-t {	
	color: #ffffff !important;
}

.ita-kd-dropdown-menu {
	background: #000000 !important;
	border-color: #ffffff !important;
}

.ita-kd-menuitem:hover {
	color: black !important;
	background-color: #167AC6 !important;
}

.ita-kd-menuitem {
	color: white !important;
}

/* languages menu */
.goog-menu {
	background: #000000 !important;
	color: white !important; 
	border-color: #ffffff !important;
	border-radius: 4px !important;
}

.goog-menuitem {
  color: white !important; 
}

.goog-menuitem-emphasize-highlight {
	background-color: #D7A61B !important; /* orange */
}

#gt-sl-gms-menu .goog-menuitem-group .goog-option-selected, 
#gt-tl-gms-menu .goog-menuitem-group .goog-option-selected {
	background-color: #167AC6 !important;
	background-image: none !important;
	font-weight: bold !important;
	color: white !important; 
}

.goog-option-selected .goog-menuitem-content {
	color: #ffffff !important;
}

/* footer */
#gt-ft-res {
	background: #333230 !important;
}

#gt-feedback {
	color: #ffffff !important;
}

/* end google translate */
}
