css:
@-moz-document url(https://accounts.google.com/ServiceLogin) {
body{background-image:url('http://i.imgur.com/mpBZVHF.png');background-color:black;background-repeat:no-repeat;background-attachment:fixed;background-position:right;background-size: auto 130%}
.signin-card{opacity:.5;border-radius:10px}
.google-header-bar, .banner, .sign-in ~ *, .google-footer-bar, #link-forgot-passwd, .one-google{display: none !important}
.wrapper {width:100%!important;height:100%!important;display:table!important}
#link-forgot-passwd{margin-left:0px!important;color:#000}
#signIn{background:black!important;color:#fff!important;border:none!important;font-family:Verdana!important}
#Email, #Passwd{border:1px solid #ed1c24 !important}
#Email:active, #Email:focus,#Passwd:active, #Passwd:focus{color:#666666!important;box-shadow:inset rgba(159,33,39,0.2) 0px 2px 5px 2px !important}
#Email:hover, #Passwd:hover{color:#666666!important;box-shadow:inset rgba(138,138,138,0.7) 0px 2px 5px 2px !important}
.main.content{width:100%!important;display:table-cell!important;vertical-align:middle!important;min-width:0px!important;max-width:0px!important;padding:0px!important}
#sign-In{background:black!important}
.sign-in{Border:4px solid rgba(138,138,138,0.7) !important;float:none!important;margin:0 auto !important;width:769px!important;min-width:450px!important}
#Email, #Passwd{Border:1px solid black !important}
.signin-box{margin:0px!important}
a, a:hover, a:visited {color:white;opacity:.7}
#profile-img, .profile-name, #reauthEmail{opacity:1!important}
}
