css:
/* Ylilauta Icy Terminal Style, version 1.3 unoffical*/
/* 23.05.15 updated for ylikaani by des*/

@namespace
 url(http://www.w3.org/1999/xhtml);

@-moz-document
 url-prefix(http://ylilauta.org),
 url-prefix(http://ylilauta.fi),
 url-prefix(http://boards.ylilauta.org),
 url-prefix(https://ylilauta.fi),
 url-prefix(https://ylilauta.org),
 url-prefix(https://boards.ylilauta.org),
 domain("www.ylilauta.org") {

/* Page background */
* {
	font-size: 11px !important;
	font-family: 'cure.se', Courier, 'cure.se', monospace !important;
}
body {
	color: #9a586c !important;
	background: #1B1B1B !important;
}

h1 {
	color: #9a586c!important;
	background: #1b1b1b !important;
}

p   {
    background: #1b1b1b !important;
}

div {
    background: #1b1b1b !important;
}


/* The lines between posts */
hr {
	border-bottom: 1px solid #1B1B1B !important;
}

/* Links */
a {
	color: #9a586c !important;
        font-size: 10px !important;
}
a:hover {
	color: #7773ac !important;
}

/* Tooltips, like message preview hovering */
.tooltip {
	border: 1px dotted #000000 !important;
	background-color: #1B1B1B !important;
}

/* The sliding-down settings-box */
#settingsbox {
	background-color: #1B1B1B !important;
	border: 1px solid #1b1b1b !important;
}

/* Sidebar */
#left {
	background: #1b1b1b !important;
}
#left ul a:hover {
	background: #9a586c !important;
	border-radius: 2px !important;
}
#left li.header {
	background-color: #1b1b1b !important;
	color: #1b1b1b !important;
	-webkit-border-radius: 2px !important;
	-o-border-radius: 2px !important;	
	-moz-border-radius: 2px !important;
	border-radius: 2px !important;
	font-weight: bold !important;
}

/* Front page */
#front h3 {
	background-color: #1B1B1B !important;
	color: #9a586c !important;
}
#front h3 a {
	color: #9a586c !important;
	text-decoration: none !important;
}
#front h3 a:hover {
	color: #000 !important;
	text-decoration: underline !important;
}
.box {
	background-color: #1B1B1B !important;
}
#tabs li {
	background-color: #1B1B1B !important;
	border-top: 1px solid #1B1B1B !important;
	border-right: 1px solid #1B1B1B !important;
}
#tabs li:first-of-type {
	border-left: 1px solid #1B1B1B !important;
}
#tabs li.cur {
	font-weight: bold !important;
	padding-top: 6px !important;
}

/* This message will be sent as a reply ... */
.infobar {
	background-color: #9a586c !important;
	color: #000 !important;
	font-weight: bold !important;
}

/* The posting form */
#postform td.label {
	border: 1px solid #1b1b1b !important;
	background-color: #1B1B1B !important;
	color: #667450 !important;
	font-weight: bold !important;
}

/* Posts */
.file p {
	border: 2px dashed #1b1b1b !important;
}
.omitted {
	color: #9a586c !important;
}
.answer {
	padding: 0 0 5px 0 !important;
	padding-left: 8px !important;
	padding-top: 4px !important;
	padding-right: 16px !important;
	background-color: #1b1b1b !important;
	border-color: #1b1b1b !important;
}
.highlighted {
	border: 3px dashed #9a586c !important;
}
.fileinfo a {
	text-decoration: none !important;
}
.postsubject {
	font-weight: bold !important;
	color: #9a586c !important;
}
.postername {
	color: #9a586c !important;
	font-weight: bold !important;
}
.sage {
	color: #2a4453 !important;
	text-decoration: underline !important;
}
.postertrip {
	color: #9a586c !important;
}
.quote {
	color: #00AA00 !important;
}
.bluequote {
	color: #1874CD !important;
}
.spoiler {
	color: #303030!important;
	background-color: #101010 !important;
}
.spoiler a {
	color: #303030 !important;
}
.spoiler:hover .quote {
	color: #7773ac !important;
}
.spoiler:hover a {
	color: #7773ac !important;
}

/* Board pagination on the bottom of the page */
.pagination {
	background-color: #1B1B1B !important;
	border: 1px solid #1B1B1B !important;
}
.disabled {
	color: #9a586c !important;
}

/* Banned -page */
#banned {
	background-color: #1B1B1B !important;
}
#banned h1 {
	background-color: #9a586c !important;
	color: #1b1b1b !important;
}

/* At least hidden threads -page uses this */
.list td {
	border: 1px solid #9a586c !important;
}
.list .threadinfo {
	border-top: 1px solid #9a586c !important;
}

/* Followed threads -box */
#followedtitle {
	background-color: #1B1B1B !important;
	color: #9a586c !important;
}
#followedbg {
	border: 1px dotted #1B1B1B !important;
	background-color: #1B1B1B !important;
}
#followedfunctions {
	border-top: 1px solid #1B1B1B !important;
}
#right .boardheader {
	border-bottom-color: #1b1b1b;
}
#right .navigationbar {
	border-top-color: #1b1b1b;
	border-bottom-color: #1b1b1b;
}
#right .thread .op_post .postinfo {
	border-bottom-color: #1b1b1b;
	border-top-color: #1b1b1b;
}
#right .thread .postinfo {
	border-bottom: 1px solid #1b1b1b;
	
}
#right .thread .replies {
	border-top: 1px solid #1b1b1b;
}
#right .thread {
	border: 1px solid #9a586c;
}
.infobar {
	display:none;
}

#right form#post label.label {
	background-color: #1b1b1b;
	border: 1px solid #9a586c;
}
input, select, textarea, button {
	border: 1px solid #9a586c;
	color: #9a586c;
}
#right form#post #postoptions {
	border: 1px solid #9a586c;
}
#tlist_threads .tlist_thread .postsubject {
	
	border-bottom-color: #1b1b1b;
	background-color: #1b1b1b;
}
#tlist_threads .tlist_thread {
	border: 1px solid #9a586c;
}
#front #tabs {
	border-bottom: 1px solid #9a586c;
}
#front .news .newstext {
	display:none;
}
#front .box {
	border: 1px solid #9a586c;
}
#right.nosidebar .nosidebar-menu ul:not(.boardlist) {
	font-weight: normal;
}
#right .boardheader h1 .nsfwinfo {
	color: #7773ac;
}
#right form#post.asyncreply {
	border: 1px solid #9a586c;
	background-color: #1b1b1b;
}
#right.preferences #tabchooser li.cur {
	background: linear-gradient(to bottom, #1b1b1b, rgba(27, 27, 27, 0)) repeat scroll 0% 0% transparent;
	border-color: #9a586c #9a586c -moz-use-text-color;
}
#right.preferences #tabchooser li {
	border-bottom: 1px solid #9a586c;
}
#right.preferences #tabchooser li.tab:hover:not(.cur) {
	background: linear-gradient(to bottom, rgba(27, 27, 27, 0.5), rgba(27, 27, 27, 0)) repeat scroll 0% 0% transparent;
}
#right .thread .postsubject a {
	font-weight: normal;
}
#right .thread .post a, #right .thread .replies a {
	font-weight: normal;
}
#right .thread .replies {
	font-weight: normal;
}

}
