css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.upwork.com") {
  .font-gotham-bold, .logo, .body-content a, .accordion .content h4
  {
    font-family: 'Open Sans', "Arial", sans-serif;
    font-weight: normal;
  }
.oPageCentered {
  
    width: 1280px;
}
  .text-primary { color: #333; }
  .oBtnPrimary, .oBtnVisitorPrimary { background: #009FF0; }
  .oHeader .oTwizzlr .oNavTab.isCurrent { color: #009FF0; }
  .oHeader .oTwizzlr .oNavTab { font-family: Arial, sans-serif; }
  .oHeader .oTwizzlr .oSecondaryNavList a { font-family: Arial, sans-serif; }

  /* Search Result Job Title */
  body { font-family: Arial, sans-serif; }

p {
    font-size: 15px;
    line-height: 23px;
}
  /* Search Result Job Title */
  a, .oBtnTertiary, .oBtnCancel, .oBtnPopup, .oThumbList figcaption, .oMoreSmall, .oLink { color: #009FF0; font-family: Arial, sans-serif; }

article {
  position: relative;
 
}
.jsPaid {
    position: absolute;
    left: 0px;
    top: -13px;
}
.jsFeedback {
    position: absolute;
    left: 75px;
    top: -10px;
} 
.jsAutoRelativeTime{
    display: block;
    position: absolute;
    top: 16px;
    right: 0px;
    font-size: 16px;
    color: lightblue;
}  
#jsTier  , .jsBudget{
    color: blue;
    font-size: 1.5em;
}
.jsType {
    color: red;
    font-size: 1.6em;
}  
.oDescription {
    margin-bottom: 13px;
    line-height: 1.6em;
    font-size: 17px;
    font-family: 'Open Sans';
}
 
.oRowTitle {
  font-size:1.7em;
   font-family: 'Open Sans' !important;
  padding-top: 10px;
 ;
}
}
