css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("prohardver.hu") {

/*
for PH!mod: "red alert" ver. 0.8.5 by sz.balazs.95
created: 2016.02.26
published: 2016.03.01

red: #a61f22;
light gray: #f3f3f3;
gray: #939598;
dark gray: #58595b;
*/

#head .logo {width: 320px; left: 17px;}
#head .logo a {outline: 0px none; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/bannera-prohardver-1.1.png) no-repeat scroll 50% 50%;}
#head .logo a span {color: transparent;}
#network li.ph {background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/redcube-2.0.png) 0 0 no-repeat;}
#network li.ma, #network li.it, #network li.gp, #network li.lo, #network li.sg {background: url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/rcm-1.0.png) 4px 8px no-repeat;}
img[src="/design/headicon-rss_ph.gif"] {width: 0px !important; height: 0px !important; background: #a61f22 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/rss-1.2.png) no-repeat scroll 0% 0%; padding: 0px 0px 19px 19px;}
.forumok .for_52 .title a, .forumok .for_53 .title a, .forumok .for_54 .title a, #left .sections .sec_14 a, #left .sections .sec_87 a, #left .sections .sec_88 a {color: #a61f22;}
#right .kbacomm, #right .kbacomm h4 {background-image: url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/book-1.2.png); background-color: #a61f22; color: #f3f3f3;}
#right .kbacomm div {color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/pat-dot-1.0.png); font-size: 16px; padding-bottom: 8px; text-align: center;}
#right .kbacomm li, #right .kbacomm::after, #right .thrcomm, #right .thrcomm::after {background: transparent;}
#right .thrcomm h4 {background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/pat-dot-1.0.png); color: #000; font-size: 13px; text-align: center; padding-top: 4px;}
#main .boxl_coloured h4 {background: #a61f22 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/menu-m-1.0.png) left center no-repeat; color:#f3f3f3; text-align: center; font-size: 13px; font-weight: bold;}
.defcnt .tabla thead th, .defcnt .tabla thead td {background-color: #58595b;}
.defcnt .tabla thead td {color: #f3f3f3;}
#top .weekendbox {width: 466px;}
.megabox {margin-right: 20px !important; margin-left: 20px !important;}
.megabox h1 {width: 205px;}
.relcnt h4, .relcmp h4 {font-size: 18px; color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/blackline-1.1.png) repeat-x scroll 0px 14px;}
}

@-moz-document domain("m.prohardver.hu") {

#head {border-bottom: 0.4em solid #a61f22; background: #000 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/background-ph-1.2.png) no-repeat scroll 0px 0px; height: 3.9em;}
#head div.logo a img {display: none;}
}
