css:
@-moz-document url-prefix('https://www.gog.com/account'), domain('chat.gog.com') {
  h2.room__name {
    margin-top: 5px;
  }
  .avatar {
    border-radius: 0 !important;
  }
  .messages {
    padding-bottom: 110px;
  }
  .message__header__time {
    color: #404040;
  }
  .message--old {
      opacity: 1;
  }
  .page-header {
      padding-top: 0;
  }
  .send-message__input {
    height: 90px;
  }
  .room {
    box-shadow: 0 -1px 0 rgba(0, 0, 0, 0.12);
    height: 52px;
  }
  .room__avatar {
    opacity: 0.7;
    top: 5px;
  }
  .room.is-current {
    background: rgba(255, 255, 255, 0.8) none repeat scroll 0 0;
  }
  .user-name {
    text-transform: none;
  }
}
