css:
@-moz-document domain(tumblr.com) {
    /* your code goes here */



.identity {
    background: #FCFCFF none repeat scroll 0% 0%;
}
.post_avatar {
    border-radius: 3px;
    background-color: #FCFCFF;
    text-decoration: none;
    position: absolute;
    overflow: visible;
    display: block;
    height: 64px;
    width: 64px;
    left: -85px;
    top: 0px;
}
div, span, applet, object, iframe, h1, h2 {
    margin: 0px;
    padding: 0px;
    border: 0px none;
    font: inherit;
    vertical-align: baseline;
}


.l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .l-content {
    background-color: #FCFCFF;
}


.identity .controls_section li {
    border-top: 1px solid rgba(110, 100, 100, 0.1);
    position: relative;
    padding: 0px;
}



.sidebar_link {
    text-decoration: none;
    line-height: 26px;
    font-size: 12px;
    display: block;
    color: rgba(0, 0, 0, 0.5);
    margin: 0px 3px;
}
.l-header-container--refresh {
    box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.13);
    transform: translateZ(0px) translateY(0px);
    z-index: 90;
    background-color: #101011;
    position: fixed;
    width: 100%;
}



.identity .controls_section li.section_header {
    background-color: rgba(255, 255, 255, 0.7);
    text-transform: uppercase;
}

.controls_section li.section_header {
    color: rgba(33, 34, 28, 0.3);
    line-height: 25px;
    font-weight: 700;
    padding: 0px 10px;
    font-size: 12px;
}




  
  .l-container.l-container--two-column-dashboard .left_column {
    background-color: #FCFCFF;
}
  
  
 
.l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column {
    background-color: #FCFCFF;
    width: 229px;
}
  
  .identity .controls_section .control-anchor {
    color: rgba(54, 51, 51, 0.5);
    line-height: 36px;
    font-weight: 700;
    display: block;
}
  
}
