css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("https?:\\/\\/ascii\\.jp\\/elem\\/.*") {
/*======================================
  weekly ascii ja jp liquid compact 2015/05
  url-prefix("http://ascii.jp/elem/")
  ======================================*/
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#jpWrap {width: auto !important; min-width: auto !important;}


/*--- header ---*/
#jpWrap #header {width: auto !important;}
#jpWrap #header #logoArea.cfx {margin: 0 !important;}
#jpWrap #header #logoArea.cfx #jplogo {width: auto !important;}
#jpWrap #header #logoArea.cfx h1,
#jpWrap #header #logoArea.cfx h2
 {background-size: contain !important; height: 1.4em !important; margin: 0 !important; padding: 0 !important;}
#jpWrap #header #logoArea.cfx #jplogo .search,
#jpWrap #header #logoArea.cfx #jplogo ul.readersMenu.cfx {float: left;}
#jpWrap #header .search .txtBoxSize {height: auto !important;}

/*--- footer ---*/
#jpWrap #footer {width: auto !important; padding: 0 !important;}
#jpWrap #footer ul {width: auto !important; height: auto !important; margin: 0 !important;}
#jpWrap #footer ul#footMenu li {display: inline !important; float: none !important;}
#jpWrap #footer ul#footMenu li:last-child {border: medium none !important;}
#jpWrap #footer address {padding: 0 !important;}

  /*--- main contents ---*/
#jpWrap #container {width: 100% !important; margin: 0 -340pt 0 0 !important; padding: 0 !important;}
#jpWrap #container #mainC {width: 100% !important; margin: 0 0 0 -90pt !important;}
#jpWrap #container #mainC>* {width: auto !important; margin: 0 250pt 0 90pt !important; padding-left: 0.5em !important;}
#jpWrap #container #sideL {width: 90pt !important;}
#jpWrap #container #sideL * {max-width: 90pt !important;}
#jpWrap #sideR {width: 250pt !important; margin: 0 !important;}
#jpWrap #sideR * {max-width: 250pt !important;}
}
