css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vybryk.com") {
  body {background:#000}
  .menu_1, footer {background:#1F2C32}
  a {color:#999 !important}
  a:hover {color:#888 !important}
  a:active {color:#3C5661 !important}
  .logo {filter:brightness(0.5) grayscale(1)}
  .post {background:#233238}
  .sort_by {background:#1F2C32; border:0}
  .bootstrap-select {background:#172125 !important; border-color:#2F434B !important}
  .btn-default {background:#172125}
  li.selected a {background:#233238 !important}
  div.dropdown-menu {background:#172125 !important; border-color:#2F434B !important}
  .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .bootstrap-select.btn-group .dropdown-menu li {background:#1D2A2F !important}
  .wpapper_form {background:#233238 !important}
  h1, h2, h3, .header-sidebar .name_user, .title_sidebar_user, .letter, .title_post, .text_blocks_setting, .subscriber_name, .nothing-found-text {color:#B5BEC3 !important}
  .info_post {background-image:none; margin-bottom:0}
  .sidebar {background:#233238; padding-bottom:10px}
  .header-sidebar .quantity_user, .user-avatar-without-circle-border .quantity_user {background-color:#1F2C32; padding:0 15px 0}
  .header-sidebar .add_post, .open_post, .info_comment .reply_comment, .info_comment .show_comment, .btn_save, .select2-selection__choice, .tab_body .btn_tab, #password-recovery-form .btn_tab {background:#31464E !important; color:#fff !important; transition:.3s}
  .header-sidebar .add_post:hover, .open_post:hover, .block_comment_post .reply_comment:hover, .block_comment_post .show_comment:hover, .btn_save:hover {background:#2B3D44; color:#fff !important; transition:.3s}
  .header-sidebar .add_post:active, .open_post:active {background:transparent; color:#fff !important; box-shadow:0 0 0 5px #2B3D44 inset; transition:.3s}
  .footer_post {background:#192428}
  .tags {background:#233238 !important; border-color:#31464E !important; color:#999 !important}
  
  .block_top_comment, .top_tags {background:transparent !important; border-color:#2F434B !important}
  .title_comment span {background-color:rgba(255,255,255,.1) !important}
  .footer_comment, .link_top_tags_2 {background:#2F434B !important; color:#666}
  .post_up {filter:grayscale(1)}
  
  .line_1 {background:transparent}
  .advice {display:none}
  
  .block_tags {border-color:#31464E}
  
  .custom_tab .nav-tabs li.active a {background:#233238 !important; border-color:#2F434B !important}
  .custom_tab .nav-tabs li a {background:#1F2C32 !important; border-color:#233238 !important}
  
  .custom_tab .filter.active {background:#2F434B !important; color:#fff}
  .custom_tab .filter {background:#172125 !important; color:#999}
  
  .blocks_comment {background:#233238; border:0}
  .info_comment {border-color:#2F434B}
  .info_comment:after {display:none}
  .info_comment:before {display:none}
  
  .main_data, .security, .custom_profile {background:#233238; border:0}
  .block_change_ava {border-color:#2F434B}
  .form_main_data, .tab_body .form-group input.form-control, .form-group.form-group_select.field-user-sex, .tab_body .form-group .form-control, .change_password {background:#233238 !important; border-color:#2F434B !important; color:#999 !important;}
  .btn_security {filter:grayscale(1) brightness(.8)}
  
  .slider-track {background:#2F434B !important}
  .slider-handle {background:#47646F}
  
  input.ios-switch[type="checkbox"] + div  {background:#2F434B; border-color:#47646F}
  .custom_profile input.bigswitch.ios-switch[type="checkbox"] + div > div {background:#47646F}
  
  .tab_body {background:#233238}
  .blocks_user {border:0 !important}
  .header_subscriber {border-color:#2F434B}
  .info_subscriber {border-color:#2F434B; background:#2F434B}
  .wpapper_user .info_subscriber, .subscriptions .info_subscriber {border:0}
  time, .text_info_subscriber {color:#999 !important}
  .button_ignor {background:#2F434B; border-radius:5px}
  .button_ignor:hover {background:#233238; border-radius:5px}
  .button_subscriber {background:#3B535D; border-radius:5px}
  .button_subscriber:hover {background:#2F434B; border-radius:5px}
  
  .alert-danger {background:#415C66; color:#fff; border-color:#4D6D79}
  
  .select2-container--default .select2-selection--multiple, .select2-selection, .select2-dropdown {background:#233238; border-color:#2F434B !important; color:#999}
  #post-captcha-image {filter: grayscale(1) invert(1)}
  
  .block_add_comment {background:#233238}
  .add_comment::after, .add_comment::before {display:none !important}
  textarea.form-control {background:#2F434B !important}
  .add_comment {border-color:#2F434B !important}
  .add_comment textarea {color:#999 !important}
  .add_comment [class^="btn"] {filter:grayscale(1)}
  .vertical_line {background:#2F434B}
}
