css:
@-moz-document domain("funstream.tv") {

* { outline: none }

/* Скрытые элементы
------------------------------------------------------------------------------*/
.play_img,
.fa-star-o:before,
.ps-scrollbar-x-rail,
.popup_smiles::before,
#left_nav li .room:before,
#left_nav li .sc2tv-forum:before,
.container-fluid .mediazone .top-block .fast-icons .icon:before,
.right_aside .content .chat-wrapper .chatTools-container .dialog_list_block.active::before,
.stream-page .top_streams_list /*рекомендованные стримы*/ {
	display: none !important
}

/* Замена шрифтов фильтра
------------------------------------------------------------------------------*/
@font-face {
