        font-style: normal;
        font-weight: 400;
        src: local('Arimo'), url(http://fonts.gstatic.com/s/arimo/v9/VF6T-UwCT6WyIiUKP6AykQ.woff2) format('woff2');
        unicode-range: U+0370-03FF;
}

