        font-style: normal;
        font-weight: 400;
        src: local('Arimo'), url(http://fonts.gstatic.com/s/arimo/v9/V41d6938Z8eBLYL302F8Ig.woff2) format('woff2');
        unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}

@font-face {
        font-family: 'Arimo';
        font-style: normal;
        font-weight: 400;
        src: local('Arimo'), url(http://fonts.gstatic.com/s/arimo/v9/HNu4Nzh6q8Es60EMNZhs9w.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

.container-fluid .mediazone .top-block .filter .wrapper li { font-family: 'Arimo', sans-serif; }

/* Изменение высоты элементов
------------------------------------------------------------------------------*/
.container-fluid .mediazone .top-block .fast-icons .icon,
.container-fluid .mediazone .top-block .filter .wrapper ul,
.container-fluid .mediazone .top-block .filter .wrapper .icon,
.container-fluid .mediazone .top-block .filter .wrapper li::after,
.container-fluid .mediazone .top-block .filter .wrapper li p {
	height: 40px
}

.container-fluid .mediazone .top-block .filter .wrapper .icon,
.container-fluid .mediazone .top-block .filter .wrapper li p {
	line-height: 40px
}

/* Сегментирование чата
------------------------------------------------------------------------------*/
.chat-container .chat_msg:nth-child(odd) { background-color: #1b1e24 }

/* Изменение стилей левой колонки сайта
------------------------------------------------------------------------------*/
#left_nav li i {
	background-repeat: no-repeat;
        background-position: 50%;
}
/*Home*/
#left_nav li .home { background-image: url(http://i.imgur.com/JT9T0j4.png) !important }

/*Streams*/
#left_nav li .stream { background-image: url(http://i.imgur.com/dDRNgZ1.png) !important }
#left_nav li .stream.active { background-image: url(http://i.imgur.com/vCGjFqq.png) !important }

/*Forum*/
#left_nav li .sc2tv-forum {
        background-image: url(http://i.imgur.com/0lv0Cal.png) !important;
	background-size: 50px;
}

#left_nav li .sc2tv-forum.active { background-image: url(http://i.imgur.com/U7lz0hH.png) !important }

/*Room*/
#left_nav li .room { background-image: url(http://i.imgur.com/A5x29xw.png) !important }
#left_nav li .room.active { background-image: url(http://i.imgur.com/NtLTHEc.png) !important }

/* Замена иконки избранного
------------------------------------------------------------------------------*/
.subscribe_block .fa-star-o,
.container-fluid .mediazone .top-block .fast-icons .icon {
	width: 50px
}

.container-fluid .mediazone .top-block .fast-icons .icon {
	margin-top: 0;
	background-color: #10151C !important;
}

.fa-star-o {
  	background: url(http://i.imgur.com/L1PS2xN.png) no-repeat;
	background-position: 50% 50% !important;
  	background-size: 28px !important;
	border-radius: 5px;
}

.fa-star-o.active { border: 2px solid #ffdb5f !important }

/* Положение счетчика стримов в избранном
------------------------------------------------------------------------------*/
.container-fluid .mediazone .top-block .fast-icons .icon .number-online {
        bottom: 28px;
        right: -4px;
}

.container-fluid .mediazone .top-block .fast-icons .icon.active .number-online {
        bottom: 26px;
        right: -6px;
}

/* Затемнение иконок над плеером
------------------------------------------------------------------------------*/
.subscribe_block,
.milestone-public-list-action {
	opacity: 0.3;
	transition: all 0.3s;
-webkit-transition: all 0.3s;
}

.subscribe_block:hover,
.milestone-public-list-action:hover {
        opacity: 1;
}

.player_switcher_block .fa-cog {
        color: #2F353B;
        transition: all .3s;
}

/* Стили фильтра трансляций
------------------------------------------------------------------------------*/
.container-fluid .top-block { height: 55px }
.container-fluid .mediazone .top-block { background: #1E252E }
.container-fluid .mediazone .top-block .filter { padding-left: 85px }
.container-fluid .mediazone .top-block .filter .wrapper li p { color: #FAFAFB }
.container-fluid .mediazone .top-block .filter .wrapper .icon { margin-left: -5px }

.container-fluid .mediazone .top-block .filter .wrapper ul {
	background: #10151C;
  	border-radius: 4px;
}

.list-inline li:first-child:hover {
	border-top-left-radius: 4px;
	border-bottom-left-radius: 4px;
}

/* Стили списка не поместившихся категорий фильтра
------------------------------------------------------------------------------*/
.container-fluid .mediazone .top-block .others.icon { height: 10px }

.container-fluid .mediazone .top-block .others.icon .list-full {
	top: 30px;
  	right: -32px;
}

/* Scale анимация списка не поместившихся категорий фильтра
------------------------------------------------------------------------------*/
.list-full.list-unstyled.scrollbar.ng-hide {
	display: block !important;
  	opacity: 0;
-webkit-transition: all 0.2s ease;
-webkit-transform-origin: right top 0px;
-webkit-transform: scale(0);
}

.list-full.list-unstyled.scrollbar {
	opacity: 1;
-webkit-transition: all 0.2s ease;   
-webkit-transform-origin: right top 0px;
-webkit-transform: scale(1);
}

/* Перемещает иконку написать стримеру налево
------------------------------------------------------------------------------*/
.right_aside .content .chat-wrapper .chatTools-container .actions .icon.to_streamer { float: left }

/* Изменить размер первых трех превью стримов
------------------------------------------------------------------------------*/
.ps-container .list-wrapper .block:nth-child(-n+3) { min-width: 33.33% !important }

/* Убрать задержку при hover'е на превью и скорость перехода .3 сек 
------------------------------------------------------------------------------*/
.container-fluid .mediazone .streams-list .list-wrapper .block .preview-container img,
.container-fluid .mediazone .streams-list .list-wrapper .block .preview-container .stream-thumb {
  	transition-duration: .3s;
  	transition-delay: initial;
}

.container-fluid .mediazone .streams-list .list-wrapper .block .preview-container,
.container-fluid .mediazone .streams-list .list-wrapper .block .stream-info {
	padding: 0 5px
}

/* Добавить scale анимацию панели смайлов
------------------------------------------------------------------------------*/
.popup_smiles.popup_clickclose.no-select.ng-hide {
        display: block !important;
        opacity: 0;
-webkit-transition: all .2s ease;
-webkit-transform-origin: bottom right 0px;
-webkit-transform: scale(0);
}

.popup_smiles {
        bottom: 116px !important;
        opacity: 1;
-webkit-transition: all .2s ease;
-webkit-transform-origin: bottom right 0px;
-webkit-transform: scale(1);
}

/* Ширина вкладок панели смайлов
------------------------------------------------------------------------------*/
.chat-wrapper .chatTools-container .popup_smiles .pagination li { width: 33.33% }

/* Стили списка каналов чата
------------------------------------------------------------------------------*/
.right_aside .dialog_list .wrapper { top: -3px !important }

.right_aside .dialog_list .wrapper .row.active,
.right_aside .dialog_list .wrapper .row:hover {
      position: relative
}

.right_aside .dialog_list .wrapper .row .channel_name { float: initial }

.right_aside .dialog_list .wrapper .row.active:after {
    position: absolute;
    content: "";
    background: #ffdb5f;
    top: 20px;
    right: 15px;
    width: 6px;
    height: 6px;
    border-radius: 100%;
}

/* Slide анимация появления списка каналов чата
------------------------------------------------------------------------------*/
.right_aside .dialog_list.active {
-webkit-animation-name: channels !important;
-webkit-animation-duration: 150ms !important;
        animation-name: channels !important;
        animation-duration: 150ms !important;
        transition-timing-function: ease-in;
}

@-webkit-keyframes channels {
        from {transform: translate3d(140px, 0, 0) }
        to {transform: translate3d(0, 0, 0) }
  }

@keyframes channels {
        from {transform: translate3d(140px, 0, 0) }
        to {transform: translate3d(0, 0, 0) }
}

/* Slide анимация появления сообщения чата
------------------------------------------------------------------------------
.chat_msg {
-webkit-animation-name: chat-ani !important;
-webkit-animation-duration: .25s !important;
-webkit-animation-iteration-count: 1 !important;
        animation-name: chat-ani !important;
        animation-duration: .25s !important;
        animation-iteration-count: 1 !important;
}

@-webkit-keyframes chat-ani {
        from {transform: translate3d(200px, 0, 0);}
        to {transform: translate3d(0, 0, 0);}
  }

@keyframes chat-ani {
        from {transform: translate3d(200px, 0, 0);}
        to {transform: translate3d(0, 0, 0);}
}
*/

/* Убирает прозрачность сообщений со сторонних сервисов
------------------------------------------------------------------------------
.right_aside .content .chat-wrapper .chat-container .chat_msg.third_party { opacity: initial; }*/

/* Изменение стилей Y скроллбара
------------------------------------------------------------------------------*/
.ps-scrollbar-y-rail { right: 0 !important; }

.ps-container>.ps-scrollbar-y-rail,
.ps-container>.ps-scrollbar-y-rail>.ps-scrollbar-y {
        width: 6px;
        border-radius: 0;
-webkit-border-radius: 0;
}

/* @media стили
------------------------------------------------------------------------------*/
@media (max-width: 768px) {
	
	#left_nav,
	#workarea {
		display: none
	}

        .right_aside {
        	min-height: 100% !important;                                                                                
	}
        
	.right_aside .content .chat-wrapper .chatTools-container .dialog_list_block { width: 100% !important }
	.right_aside .content .chat-wrapper .chatTools-container .dialog_list_block .channel_name { margin-right: 100px }
        
}


/* chat message block
------------------------------------------------------------------------------*/
.chat-wrapper .chatTools-container .text_block_wrapper .text_block .text,
.chat-wrapper .chatTools-container .text_block_wrapper .text_block .text-ban-cap,
.chat-wrapper .chatTools-container .text_block_wrapper .text_block {
	background-color: #fff;
}

.user_msg a { color: #7fcdff }
.container-fluid .streams-list .block { min-height: initial }
.container-fluid .streams-list .block .preview-container { min-width: initial }
.chat-wrapper .chatTools-container .text_block_wrapper { padding: 0 9px 10px }
.chat-wrapper .chatTools-container .popup_smiles .pagination li { padding: 0 8.8px; }
.chat-wrapper .chatTools-container .text_block_wrapper .text_block { border-radius: 4px }
.chat-wrapper .chatTools-container .text_block_wrapper .text_block { border-color: #fff }
.chat-wrapper .chatTools-container .text_block_wrapper .text_block .text { color: #333 }

}
