css:
@-moz-document domain("just-dice.com") {
body { 
background: #000;
color: white;
}

th	{
color: #fff;
}

.bal_text	{
color: #fff;
}

.panel, .results_container {
  border-radius: 0px;
  background: #000;
  border: 2px solid #444;
  color: white;
  margin: 0px;
}

.results {
    color: #fff;
    background: #000;
}

.results .when {
  font-size: 85%;
}

.betid a {
    color: #fff;
  font-size: 85%;
}

.results_header {
  font-weight: bold;
  font-size: 120%;
  padding: 1ex 0;
  border-radius: 0 0 0 0;
  border-bottom: 1px solid #444;
  background: #000;
  color: #fff;
}

.stats {
  border-left: 3px solid #000;
  border-right: 3px solid #000;
  border-top: 3px solid #000;
  background: #000;
  border-radius: 0px;
  padding: 3px 3px 2px 3px;
  margin: 5px;
  margin-bottom: 0;
  margin-right: 17px;
  color: white;
}

.tabs a {
  border-left: 1px solid #444;
  border-right: 1px solid #444;
  border-top: 1px solid #444;
  padding: 9px 10px 7px 8px;
  color: #FFF;
  border-radius: 0 0 0 0;
  font-weight: bold;
  text-decoration: none;
  margin: -3px;
  background: #000;
}

.tabs a.active {
  color: #fff;
  background: #777777;
  border-bottom: 0px;
}

.result.me {
    background: #000;
}

.chatscroll, .chatbetscroll {
  border: 1px solid black;
  background: black;
  color: white;
  overflow-y: scroll;
}

.chatemote {
  background: #494949;
}

.chatinfo {
  background: #741794;
}

.chatwin {
  background: #175A17;
}

.chatlose {
  background: #8E1717;
}

.chatinput {
  border: 1px solid #444;
  text-align: left;
  width: auto;
    color: white;
    background: black;
}

a {
  color: white;
}

.panel span {
  color: black;
  background-color: #999;
  border: 0px solid #777;
  border-radius: 0px;
  padding: 1px 4px;
}

.hist_table {
  color: black;
}

#faq h4 {
  padding: 10px;
  background: #000;
  border: 1px solid #666;
  border-radius: 0px;
}

#chat span {
  color: white;
}

.tabs a.attention {
  background: #373737;
}

.header {
  float: left;
  display: block;
      background: #000;
  border: 0px solid #777;
  border-radius: 0px;
  font-size: 2em;
  font-weight: bold;
  padding: 0.3em 0.7em;
}

.log span {
    background-color: #000;
    color: white
}

div.log {
  background: #000;
}

fieldset {
  background-color: #000;
}

.button_group {
    border: 0px;
  background-color: #000;
}

.big input {
  background: #000;
    border-radius: 0px;
}

.chatstat span {
  font-family: monospace;
  padding: 2px 4px;
  margin: 2px;
  font-weight: bold;
  background: #000;
  border: 2px solid #444;
  border-radius: 0px;
  color: white;
}

.button_group button.button_label {
  border: 2px solid #666;
  border-radius: 0px;
  background: #000;
  width: auto;
  color: white;
}

.button_group button.button_label:hover {
    border: 2px solid #666;
    color: black;
}

fieldset p {
  background: #000;
  color: white;
}

input {
  background: #000000;
  color: white;
}

.invalid {
  background-color: #5C1717;
}

input, button {
  color: white;
}

.button_group button.invalid {
  background-color: #5C1717;
  border: 4px solid #B52121;
}

.button_group button.invalid:hover {
    background-color: #5C1717;
    border: 4px solid #B52121;
    color: white;
}

input[type="submit"], button {
  background-color: #000;
}

.button_group button {
	background-color: #0C340C;
    border: 3px solid #070;
    border-radius: 0px;
}

.tabs a.attention:hover {
  background: #bbb;
}

.chatprincess {
  color: #F1A7FF;
}

.result.me {
  background: #202020;
}

.hist_table th {
  background: #3D3D3D;
  color: white;
}

fieldset, .log, .buttons {
    border: 0px;
}

.button_group {
      border: 0px solid #777;
  border-radius: 0px;
    margin-left: 5px;
    margin-right: 0px;
}

.llabel {
  border-radius: 0px;
}

.rlabel {
  border-radius: 0px;
}

.log2 {
  padding: 17px;
  margin-left: -15px;
}

button {
  border-radius: 0px;
  border: 3px solid #666;
}

button:hover {
	color: black;
}

.winlose div {
  border-radius: 0px;
  margin: 1px;
  padding: 1px;
}

.monospace {
  background: #999;
  padding: 2px;
  border-radius: 0px;
}

.mono span {
  border: 0px #777;
  border-radius: 0px;
}

.roll span {
    color: black;
    border: 0px;
    border-radius: 0px;
}

.chatpm {
  background: #262D5E;
}
}
