css:
@-moz-document domain("hotair.com") {
body {
   background-color: gray
}

/*"Headlines" list*/

div.qlinkWrapper {
   font-family: "lucida console", "deja vu mono", "liberation Mono", Monospace;
   font-size: 11px !important;
   color: blue
}
table.hltable table.hlitem a {
   color: blue
}
table.hltable table.hlitem a:visited {
   color: brown !important
}
div.headline-comment-count a {
   color: gray !important
}

/* Headline */

h3 {
   font-size: 13px
}

/* Byline*/

h4 {
   font-size: 8px
}

/*article text*/

div#page-post {
   background-color: #f2f2f2;
   font-family: "Droid Sans", Verdana, "Bitstream Vera Sans", "DejaVu Sans", "liberation sans", Tahoma, Geneva, Arial, Sans-serif !important;
   font-size: 13px;
   color: black;
   border-radius: 8px
}
blockquote {
   border-radius: 8px
}

/*caption*/

div.section p {
   font-family: "lucida console", "deja vu mono", "liberation Mono", Monospace !important;
   font-size: 9 !important;
   color: blue;
}

/*User comments*/

div#comments-section {
   background-color: lightgray
}

/* ALL HEADLINES*/

div#headlines-content ul li span.hl-title a {
   color: lightgreen
}

ul.breaking a {
   color: white
}
}
