css:
@-moz-document domain("kissanime.com") {
.barTitle {
  height: 25px;
  padding-left: 10px;
  background: #5b9003;
  font: normal 17px/23px "Tahoma",Arial,Helvetica,sans-serif;
  letter-spacing: -1px;
  color: #fff;
}
#tabmenucontainer a:link, #tabmenucontainer a:visited, #tabmenucontainer a:hover, #tabmenucontainer li a.tabactive {
  float: left;
  background: transparent url(http://i.imgur.com/u6alqfD.gif) no-repeat left top;
  background-position: 0% -25px;
}
a.next {
  background: url(http://i.imgur.com/CKcEBcp.png);
  float: left;
}
a.prev {
  background: url(http://i.imgur.com/MEMuQu4.png);
  float: left;
}
a.prev:hover {
  background: url(http://i.imgur.com/n7m4zfB.png);
}
a.next:hover {
  background: url(http://i.imgur.com/LIYQNXK.png);
}

#topHolderBox {
  color: #FFF;
  background: #353535;
  font-family: Tahoma,Verdana,Arial;
  font-size: 13px;
  border-top-right-radius: 0px;
  border-top-left-radius: 0px;
  border-bottom-right-radius: 0px;
  border-bottom-left-radius: 0px;
  z-index: 99;
  text-align: left;
  display: inline-block;
  text-align: center;
  padding-top: 8px;
}
.banner {
  margin: 0 0 10px 0;
  padding: 0px;
  width: 100%;
  background: #171616 repeat-x top;
  border: 0px solid #333333;
  height: 208px;
}
Style Attribute {
  z-index: 0;
  position: absolute;
  top: 230px;
  left: 0px;
  width: 100%;
  height: 768px;
  overflow: hidden;
  visibility: hidden;
}
#head h1 {
  margin: 15px 0px 0px 0px;
  padding: 0px;
  background: transparent url(http://i.imgur.com/pnzjas9.png) no-repeat;
  text-indent: -9999px;
  float: left;
  display: inline;
}

#head h1 a.logo {
  display: block;
  width: 450px;
  height: 130px;
  border: none;
}
#navcontainer a:link, #navcontainer a:visited {
  float: left;
  background: url(http://i.imgur.com/olLnUgh.png) no-repeat left top;
  background-position: 0% -27px;
  color: #1c1c1c;
  text-decoration: none;
}

#navcontainer li a#currentTab {
  float: left;
  background: url(http://i.imgur.com/olLnUgh.png) no-repeat left top;
  background-position: 0% 0px;
  color: #ffffff;
  text-decoration: none;
  line-height: 27px;
}
#navbar {
  width: 100%;
  background: #161616;
  height: 50px;
}
}
