css:
@-moz-document domain("zippyshare.com")
{
  body,.folderlogo,div.outer_main,.inner_main table,.content_block,.sidebar
  {
    color: #999 !important;
    background: #111 !important;
    border-color: #333 !important;
    text-shadow: none !important;
  }

  div.outer_menu,div.inner_menu,.lang ul,.login form div div input,.groupbox,#navbar,#navbar_inline,INPUT, TEXTAREA, SELECT, OPTION
  {
    background: #222 !important;
  }

  .formtail,.option1,.row1-med,.sidebar h2,.row1,table th,.option2,.option1-med,.critical,ol.commentlist li,.row2
  {
    background: #111 !important;
  }

  #navbar_inline
  {
    background-image: none !important;
  }

  .left div div font,a,.pojemnik div div,.belka font,.bold,#lrbox .left,.ticklabel,.center font,#bottom font,.inner_menu span,.login,
.login form div div input,.login form div font,div.inner_main,#uploader font,#tab1 p,h1,.groupbox,.row1-med,.sidebar h2,label, .fake_link,
INPUT, TEXTAREA, SELECT, OPTION,.row1,table th,.option2,.option1-med,.entry-content p, .entry-summary p, .comment-content p, .mu_register p,
.entry-content li, .comment-content li, .mu_register li,.comments-area h2,ol.commentlist li,.widget-title,.row2,#footer p,#respond h3#reply-title,
#respond form p,#uploader #lr #l
  {
    color: #b7b7b7 !important;
  }

  div.inner_menu,ol.commentlist li article .comment-meta a time
  {
    color: lightorange !important;
  }

  .login form div div input,.content_block,#navbar,.sidebar,INPUT, TEXTAREA, SELECT,.lang ul a,.lang li
  {
    border: solid 1px #444 !important;
  }

  ol.commentlist li
  {
    border-top-color: transparent !important;
  }

  ol.commentlist li
  {
    border-bottom-color: transparent !important;
  }

  ol.commentlist li
  {
    border-right-color: transparent !important;
  }

  ol.commentlist li
  {
    border-left-color: transparent !important;
  }

  .formtail
  {
    border-top: transparent !important;
  }

  .groupbox,.option1,.row1-med,.sidebar h2,.row1,.option2,.option1-med,.lang ul,.row2,.archive-header, .page-header
  {
    border-bottom: transparent !important;
  }

  .logo,.download,#menu #menuright,#menu #menuleft,#menu #menucenter,img[src="http://support.zippyshare.com/images/default/header.jpg"],
img[src="/images/logo.png"],img[src="http://blog.zippyshare.com/v3/logo.png"],img[src="http://support.zippyshare.com/images/default/ticket_icon.gif"],
img[src="http://support.zippyshare.com/images/default/kb_icon.gif"],img[src="uploader/safe.png"],#browse,#share_button,
img[src="uploader/download.png"],img[src="http://www.blog.zippyshare.com/AudioFolder.png"],img[src="http://blog.zippyshare.com/z.png"]
  {
    filter: invert(93%) !important;
    transition: 0.5s;
  }

  .download:hover
  {
    filter: invert(100%) contrast(2)  !important;
    transition: 0.5s;
  }
}
