css:
@-moz-document domain("funstream.tv") {

.right_aside .content .chat-wrapper .chat-container .chat_msg .user_msg img {
    
  height: 40px;
}


.right_aside .content .chat-wrapper .chat-container .chat_msg .user_msg {
 
  color: #D3D7DB;
  font-size: 18px;
  font-family: segoe ui;
  font-weight: 200;
}


.right_aside .content .chat-wrapper .chat-container .chat_msg .user_name {
  
  color: #48C7AF;
  font-size: 18px;
  font-family: segoe ui;
  font-weight: 500;
}


.right_aside .content .chat-wrapper .chat-container .chat_msg .user_name.user_name_to {
    
  color: #6F93CA;
}

.right_aside .content .chat-wrapper .chat-container .chat_msg.user_msg_to .user_msg, .right_aside .content .chat-wrapper .chat-container .chat_msg.user_msg_to .user_name.user_name_to {
  font-weight: 500;
  color: #FFDB5F;
}

.right_aside .content .chat-wrapper .chat-container .chat_msg .user_msg .user_image {
  display: block;
  max-width: 100%;
  height: 250px;
  object-fit: contain;
}

}
