    display: inline-block;
    letter-spacing: normal;
    text-rendering: auto;
    vertical-align: top;
    word-spacing: normal;
}
.yui3-u-1 {
    width: 100%;
}
.yui3-u-1-2 {
    width: 50%;
}
.yui3-u-1-3 {
    width: 33.3333%;
}
.yui3-u-2-3 {
    width: 66.6667%;
}
.yui3-u-1-4 {
    width: 25%;
}
.yui3-u-3-4 {
    width: 75%;
}
