#yui3-css-stamp.cssgrids {
    display: none;
}
h1 {
    font-size: 138.5%;
}
h2 {
    font-size: 123.1%;
}
h3 {
    font-size: 108%;
}
h1, h2, h3 {
    margin: 1em 0;
}
h1, h2, h3, h4, h5, h6, strong {
    font-weight: bold;
}
abbr, acronym {
    border-bottom: 1px dotted #000;
    cursor: help;
}
em {
    font-style: italic;
}
blockquote, ul, ol, dl {
    margin: 1em;
}
ol, ul, dl {
    margin-left: 2em;
}
ol {
    list-style: decimal outside none;
}
ul {
    list-style: disc outside none;
}
dd {
    margin-left: 1em;
}
th, td {
    border: 1px solid #000;
    padding: 0.5em;
}
th {
    font-weight: bold;
    text-align: center;
}
caption {
    margin-bottom: 0.5em;
    text-align: center;
}
p, fieldset, table, pre {
    margin-bottom: 1em;
}
input[type="text"], input[type="password"], textarea {
    width: 12.25em;
}
#yui3-css-stamp.cssbase {
    display: none;
}

}
