css:
@-moz-document domain("smashladder.com") {

body .wrapper{
    background-image: url("http://i.imgur.com/Rm4XFpx.jpg");
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

body .btn-info{
    border: none;
    border-radius: 0;
}

.is_subscribed .username, .username.is_subscribed{
    text-shadow: none;
}

/*=====Subscription Banner=====*/

#notification-bar{
    width: 10%;
    min-width: 320px;
    font-size: 14px;
    padding: 40px;
    margin-left: 40px;
}

/*=====Header=====*/

.header{
    border-bottom: none;
    background-color: rgb(21, 21, 21);
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.75);
}

.logo img{
    background-image: url(http://i.imgur.com/FxjLleC.png);
    padding: 30px;
    margin-right: -5px;
    margin-left: 5px
}

#top_main_nav{
    height: 60px;
}

.nav{
    height: 60px;
    padding: 0;
}

#top_main_nav{
    height: 60px;
    margin-top: 0px;
    margin-left: 30px;
}

.nav{
  height: 60px;
  padding: 0;
}

#top_main_nav .main_menu_items > li{
    height: 60px;
}

#top_main_nav .main_menu_items > li > a{
     line-height: 44px;
}

#top_main_nav .main_menu_items > li > a.active, #top_main_nav .main_menu_items > li.open > a, #top_main_nav .main_menu_items > li > a:hover{
    border-radius: 0;
}

.guides_and_such{
  text-align: left;
  padding: 4px 0;
  max-width: 1371px;
  margin: 0 auto;
  padding-top: 20px;
  margin-top: -22px;
  padding-left: 15px;
  padding-bottom: 15px;
  background-color: rgba(80, 80, 80, 0.95);
}

.guides_and_such .btn{
  border-radius: 0;
  border: none;
}

/*=====Current Online Players Area=====*/

.region_selectors, .sub_user_list_buttons{
	padding-top: 20px;
}

.region_button.active, .user_list_button.active{
	border: none;
}

.region_selectors button:hover{
    background-color: #000;
}

.region_selectors button{
    border-radius: 0;
    height: 30px;
    width: 60px;
    background-color: #8A8A8A;
}

.region_selectors .active{
	border-radius: 0;
    background-color: rgb(0, 203, 208);
    color: black;
}

.region_selectors button:hover{
	border: none;
}

.list_holder ul li:nth-child(odd){
    background-color: rgba(38, 38, 38, 0.99);
}

.list_holder ul li:nth-child(even){
    background-color: rgba(34, 34, 34, 0.99);
}

/*=====Matchmaking Area=====*/

.panel{
	border: none;
    box-shadow: 0 0 10px -2px rgba(0, 0, 0, 0.5);
    background-color: rgba(80, 80, 80, 0.9);
}

.user_info, .dispute_container span.message, .info_container{
	border: none;
}

.mid-side-container, .play_match_container{
	border-radius: 0;
    border: none;
    background-color: rgba(80, 80, 80, 0.95);
}

.tournament_listing .btn{
    border: none;
    border-radius: 0;
    padding: 8px;
    margin: 3px;
}

/*=====Group Chat=====*/

.user_lists ul, .chat_container{
    border: none;
	box-shadow: 0 0 15px -2px rgba(0, 0, 0, 0.8);
}

.chat_input_group .send_chat_button{
    border: none;
    border-radius: 0;
}

input[type=text]:not(.form-control), input[type=email]:not(.form-control), input[type=password]:not(.form-control), .chat_container, .cancel_dialog textarea, .info_container ul, .chat_input{
	background-color: rgba(39, 39, 39, 0.99);
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.55);
}

.chat_input{
    border: none;
    border-radius: 0;
}

#chat_tabs .chat_tab{
    border-radius: 0;
    border: none;
    padding: 6px;
    margin-left: 5px;
    margin-right: 5px;
}

#chat_tabs .chat_tab:first-child{
    margin-left: 0;
}
    
#chat_tabs .chat_tab:last-child{
    margin-right: 0;
}

#chat_tabs .name{
    width: auto;
    max-width: none;
}

#chat_tabs .chat_tab.create_chat{
    padding-left: 8px;
    padding-right: 11px;
}

#chat_tabs .chat_tab.active .closing_x{
    border-radius: 0;
}

/*=====Match Private Chat=====*/

.request button{
    border: none;
    border-radius: 0;
}

.current_match .chat_container.private{
  border-radius: 0;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.35);
}

.current_match .control-buttons button{
    border: none;
    border-radius: 0;
}

textarea.form-control, .rateit{
    border-radius: 0;
}

.game_filters{
    padding-bottom: 20px;
	border-bottom: 1px solid rgb(39, 39, 39);
}

#recent_match_searchers{
    margin-top: 20px;
}

/*=====Scroll Bars=====*/

::-webkit-scrollbar {background: rgba(0, 0, 0, 0.3);   width: 8px; border-radius: 0px;}
::-webkit-scrollbar-track {border-radius: 0px;}
::-webkit-scrollbar-thumb { background-color: gray; border-radius: 0px;}
::-webkit-scrollbar-thumb:hover {background-color: gray;}

/*=====Personal Player Info Popup=====*/

.user_info.other_user_info, #stream_action{
    background-color: rgb(16, 16, 16);
}

.main_info button{
    border: none;
    border-radius: 0;
    padding: 10px;
    margin-right: 5px;
    color: black;
}

.show_chat_controls{
    color: black;
}

.user_info.other_user_info, .popout_card{
    color: #CCC;
}

.other_user_info .last_play_date{
   color: rgb(140, 140, 236);
}

.user_info a.match{
    color: #FFF;
}

/*=====Private Chat Footer=====*/

#bottom_dock{
    border-top: none;
    min-height: 50px;
}

.float_displayed{
    border: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
    border-radius: 0;
}

.private_chat_area .closing_x{
    border-radius: 0;
}

.private_window_display{
    border-radius: 0;
    background-color: #242424;
    
}

.disconnected, .restarting_computer, .user_controls, #news_ticker, .private_window_display{
    border: none;
    margin-top: 12px;
    margin-left: 10px;
}


}
