css:
@-moz-document domain(web.whatsapp.com) {
	body {
		background-color: #222;
		background-image: none;
		background-image: none;
	}
	#startup {
		background-color: #222;
	}
	.pane-intro {
		background-color: #2d2d2d;
	}
	.pane-intro:after {
		border-top: 0px solid #58e870;
	}
	.intro-title {
		color: #f5f5f5;
	}
	.intro-text {
		color: #c4c4c4;
	}
	.intro-image {
		width: 0px;
		height: 0px;
		background-image: none;
	}
	.pane-chat, .drawer-container-right, .pane-intro {
		background-image: none;
		background-color: #2d2d2d;
	}
	.message-in {
		background-color: #3C5755;
	}
	.message-in:before {
		background-image: none;
	}
	.message-text {
		color: #f5f5f5;
	}
	.message-out {
		background-color: #3C5755;
	}
	.message-out:before {
		background-image: none;
	}
	.pane-chat-footer {
		background-color: #444;
	}
	.menu-tabs-emoji {
		background-color: #444;
	}
	.bg-chat-loaded .pane-chat {
		background-image: none !important;
	}
	.pane-header {
		background-color: #222;
	}
	.chat-title, .location-main, .header-title {
		color: #dcdcdc;
	}
	.chat-status {
		color: #dcdcdc;
	}
	.chat, .location {
		background-color: #2d2d2d;
		border-bottom: none !important;
	}
	.chatlist .chat.active, .chatlist .active.location {
		background-color: #3C5755;
		border-bottom: none !important;
	}
	.chat.active .chat-status, .active.location .chat-status, .chat.active .location-secondary, .active.location .location-secondary, .chat.active .header-secondary, .active.location 		.header-secondary {
		color: #dcdcdc;
	}
	.chatlist .chat.active:hover, .chatlist .active.location:hover {
		background-color: #4f6766;
	}
	.chatlist .chat:hover, .chatlist .location:hover {
		background-color: #565656;
	}
	.pane-list-body {
		background-color: #2d2d2d;
	}
	* {
		text-shadow: none !important;
	}
	.chatlist .chat:after, .chatlist .location:after {
		border-bottom: none !important;
	}
	.chat-meta {
		color: rgba(255, 255, 255, 0.4) !important;
	}
	.subheader-search {
		background-color: #2d2d2d;
	}
	.chatlist .is-unread .chat-title, .chatlist .is-unread .location-main, .chatlist .is-unread .header-title, .chatlist .unread .chat-title, .chatlist .unread .location-main, .chatlist .unread .header-title {
		color: #dcdcdc;
	}
	.chatlist .is-unread .chat-status, .chatlist .is-unread .location-secondary, .chatlist .is-unread .header-secondary, .chatlist .unread .chat-status, .chatlist .unread .location-secondary, .chatlist .unread .header-secondary {
		color: #dcdcdc;
	}
	.drawer-info {
		background-color: #2d2d2d;
	}
	.drawer {
		background-color: #2d2d2d;
	}
	.drawer-profile .drawer-header {
		background-color: #222;
		color: #dcdcdc;
	}
	.media-collection > span {
		background-color: #222;
	}
	.drawer-media .drawer-body {
		background-color: #2d2d2d;
	}
	.btn {
		color: #dcdcdc;
		background-color: #3C5755;
	}
	.media-viewer {
        background-color: #2d2d2d;
	}
	.media-panel-header {
        background-color: #2d2d2d;
	}
	.media-chat .chat-main, .media-chat .header-main {
        color: #c4c4c4;
	}
	.sprite-loaded .icon, .sprite-loaded .icon-s, .sprite-loaded .icon-l, .sprite-loaded .icon-xl {
        background-image: url("http://a.1339.cf/uuhmvb.svg");
	}
	.menu-icons-item {
  		background-color: #222;
    }
	.location-secondary, .header-secondary {
        color: #c4c4c4;
	}
	.list-title {
  	    color: #009688;
  	    background-color: #222;
	}
}
